import React, {useState} from 'react'
import {
    DropdownMenu,
    DropdownItem,
    DropdownToggle,
    UncontrolledDropdown,
    UncontrolledTooltip,
    Button
} from "reactstrap";
import IChat from '../alias/IChat';
import IMessage from '../alias/IMessage';
import { ModalSelectUsers } from './ModalSelectUsers';
import { useTranslation } from "react-i18next";

interface IProps {
    activeChat: IChat;
    message: IMessage;
}


const ChatMessageMenuOption: React.FC<IProps> = ({ activeChat, message }) => {
    const { t } = useTranslation();
    const deleteMessage = (id: string) => {};
    const [showModal, setShowModal] = useState(false);
  
    const toggle = () => {
      setShowModal(!showModal);
    };
  

    if (!activeChat.isTyping) {
        return (
          <UncontrolledDropdown className="align-self-start">
            <DropdownToggle tag="a">
              <i className="ri-more-2-fill"></i>
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem>
                {t("Copy")}
                <i className="ri-file-copy-line float-right text-muted"></i>
              </DropdownItem>
              <DropdownItem>
                {t("Save")}{" "}
                <i className="ri-save-line float-right text-muted"></i>
              </DropdownItem>
                <DropdownItem onClick={toggle}>
                {t("Forward")}{"  "}
                  <ModalSelectUsers show={showModal} toggle={toggle} />
                        <i className="ri-group-line mr-1 float-right text-muted"/>
              </DropdownItem>
              <DropdownItem onClick={() => deleteMessage(message.id)}>
                Delete{" "}
                <i className="ri-delete-bin-line float-right text-muted"></i>
              </DropdownItem>
            </DropdownMenu>
          </UncontrolledDropdown>
        );
    }

    return <></>
}

export default ChatMessageMenuOption;
