import React from 'react';
import { useChat } from '../contexts/ChatContext';
import { getUserStatusName } from "../helpers/StatusUtils";



const UserStatusOnProfileSidebar : React.FC = () => {
const { activeChat } = useChat();

    return <p className="text-muted text-truncate mb-1">
    {(() => {
      switch (activeChat.status) {
        case "online":
          return (
            <>
              <i className="ri-record-circle-fill font-size-10 text-success mr-1"></i>
            </>
          );

        case "away":
          return (
            <>
              <i className="ri-record-circle-fill font-size-10 text-warning mr-1"></i>
            </>
          );

        case "dnd":
          return (
            <>
              <i className="ri-record-circle-fill font-size-10 text-danger mr-1"></i>
            </>
          );

        default:
          return (
            <>
              <i className="ri-record-circle-fill font-size-10 text-secondary mr-1"></i>
            </>
          );
      }
    })()}
    {getUserStatusName(activeChat.status as string)}
  </p>
}

export default UserStatusOnProfileSidebar;
