import React, { createContext, useContext, useState } from "react";

interface IModalContext {
  showModalForwardMessageTo: boolean;
  setShowModalForwardMessageTo(value: boolean): void;
}

export const ModalContext = createContext<IModalContext>({} as IModalContext);

export const ModalProvider: React.FC = ({ children }) => {
  const [showModalForwardMessageTo, setShowModalForwardMessageTo] = useState(
    false
  );
  const [showModalEditInfo, setShowModalEditInfo] = useState(
    false
  );
  return (
    <ModalContext.Provider
      value={{ showModalForwardMessageTo, setShowModalForwardMessageTo }}
    >
      {children}
    </ModalContext.Provider>
  );
};

export function useModal() {
  return useContext(ModalContext);
}
