import React, { useState } from 'react';
import { Button } from 'reactstrap';
import { useMainBox } from '../contexts/MainBoxContext';
import ScreenType from '../enuns/ScreenType';

const Cadastros : React.FC = () => {
    const [toggleButtons, setToggleButtons] = useState<boolean>(true);
    const { setScreen } = useMainBox();
    return(
        
        <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', height: "100%", width: "100%"}}>
            {toggleButtons ? 
            
            <div>
                <Button className="ml-4" type="button" color="primary" onClick={() => {
                    setToggleButtons(false);
                }}>
                    Sou comprador        
                </Button>
                <Button className="ml-4" type="button" color="primary" onClick={() => {
                    setToggleButtons(false);
                }}>
                    Sou fornecedor
                </Button>
            </div> : 
                <div>
                <Button className="ml-4" type="button" color="primary" onClick={() => setScreen(ScreenType.CADASTROSFISICA)}>
                    Sou pessoa física        
                </Button>
                <Button className="ml-4" type="button" color="primary" onClick={() => setScreen(ScreenType.CADASTROSJURIDICA)}>
                    Sou pessoa jurídica
                </Button>
                <Button className="ml-4" type="button" color="primary" onClick={() => setScreen(ScreenType.CADASTROSFORNECEDOR)}>
                    Sou fornecedor
                </Button>
            </div>
            }
        </div>
    )
}

export default Cadastros;
