import {
  ConferenceRoom,
  ConferenceRoomParticipant,
  Message,
  Settings,
} from "@digivox/apicommunicator/dist/types/types/types";
import IChat from "../../alias/IChat";
import { IConference } from "../../alias/IConference";
import IContact from "../../alias/IContact";
import IGroup from "../../alias/IGroup";
import IMessage from "../../alias/IMessage";
import IUser from "../../alias/IUser";
import PresenceStatus from "../../enuns/PresenceStatus";
import UserType from "../../enuns/UserType";
import { getContactName, getUserProfilePicture } from "./formatUtils";

interface MessageContent {
  reply_to?: string;
  reply_msg?: string;
  reply_msg_id?: string;
}

export function getAttachmentType(extension: string) {
  const booleans = {
    isImageMessage: /jpe?g|png|gif|ico$/i.test(extension),
    isAudioMessage: /ogg|mp3|wav|m4a|webm$/i.test(extension),
    isVideoMessage: /mp4|avi|mov$/i.test(extension),
    isFileMessage: true,
  };
  booleans.isFileMessage =
    !booleans.isAudioMessage &&
    !booleans.isImageMessage &&
    !booleans.isVideoMessage;
  return booleans;
}
//Groups
export const parseGroup = (apiGroup: any) => {
  return {
    groupId: apiGroup.id,
    name: apiGroup.name,
    description: apiGroup.description,
    jid: `${apiGroup.id}@conference.${apiGroup.domainName}`,
    profilePicture: apiGroup.imageUrl,
    favorite: apiGroup.favorite,
    added: apiGroup.added,
    members: apiGroup.members.map((member: any) => {
      return {
        userId: member.id,
        name: member.name,
        profilePicture: member.imageUrl,
        role: member.admin ? UserType.ADMIN : UserType.USER,
        status: PresenceStatus.ONLINE,
      };
    }),
  };
};
//Contacts
export const parseContact = (apiContact: any) => {
  return {
    id: apiContact.id,
    jid: apiContact.imId,
    name: apiContact.name,
    department: apiContact.department,
    domain: apiContact.domainName,
    number: apiContact.extension,
    profilePicture: apiContact.imageUrl,
    email: apiContact.email,
    blocked: apiContact.blocked,
    added: apiContact.added,
    status: PresenceStatus.ONLINE,
    favorite: apiContact.favorite,
  };
};
//Chats
export const parseChats = (
  chats: any[],
  groups: Map<string, IGroup>,
  contacts: Map<string, IContact>,
  user: IUser
): IChat[] => {
  const parsedChats: IChat[] = [];
  chats.forEach((chat) => {
    const parsedChat = parseChat(chat, groups, contacts, user);
    if (parsedChat) parsedChats.push(parsedChat);
  });
  return parsedChats;
};
export const parseChat = (
  chat: any,
  groups: Map<string, IGroup>,
  contacts: Map<string, IContact>,
  user: IUser
) => {
  const chatUser = groups.get(chat.id) || contacts.get(chat.id);
  if (chatUser) {
    const chatMessage = chat.messages[0];
    const iChat: IChat = {
      id: chat.id,
      jid: chatUser.jid,
      name: chat.name,
      email: (chatUser as IContact)?.email,
      department: (chatUser as IContact)?.department,
      profilePicture: chatUser?.profilePicture || chatUser?.profilePicture,
      status: "status" in chatUser ? (chatUser as IContact).status : undefined,
      unRead: chat.unreadMessages,
      chatType: chat.chatType,
      members: (chatUser as IGroup).members,
      inputMessage: "",
      isTyping: false,
      messages: [parseMessage(chatMessage, contacts, groups, user)],
      favorite: chatUser?.favorite || chatUser?.favorite || false,
      added: chatUser.added,
      groupId: (chatUser as IGroup).groupId,
    };
    return iChat;
  }
};
//Messages
export const parseMessage = (
  chatMessage: Message,
  contacts: Map<string, IContact>,
  groups: Map<string, IGroup>,
  user: IUser
) => {
  let booleans = {
    isImageMessage: chatMessage.isImage,
    isFileMessage:
      chatMessage.isFile &&
      !chatMessage.isImage &&
      !chatMessage.isVideo &&
      !chatMessage.isAudio,
    isVideoMessage: chatMessage.isVideo,
    isAudioMessage: chatMessage.isAudio,
  };
  if (isUrl(chatMessage.message)) {
    booleans = getUrlBooleans(chatMessage.message);
  }
  let content: MessageContent = {};
  if (chatMessage.content) {
    let newContent = chatMessage.content.replace(/'/g, '"');
    try {
      content = JSON.parse(newContent);
    } catch (e) {
      content = {};
    }
    if (typeof content !== "object") {
      content = {};
    }
    if (content.reply_to) content.reply_to = content.reply_to.split("@")[0];
  }
  const msg: IMessage = {
    id: chatMessage.idXmpp,
    userName: getContactName(chatMessage, contacts, groups, user),
    userProfilePicture: getUserProfilePicture(
      chatMessage,
      contacts,
      groups,
      user
    ),
    message: chatMessage.message,
    time: new Date(chatMessage.sentAt).toISOString(),
    fileUrl: chatMessage.message,
    toUser: chatMessage.to,
    fromUser: chatMessage.from,
    deliverStatus: chatMessage.deliveryStatus,
    readStatus: chatMessage.readStatus,
    deliverTimestamp: chatMessage.deliveryTimestamp || "",
    readTimestamp: chatMessage.readTimestamp || "",
    content: chatMessage.content,
    contentType: chatMessage.contentType,
    isReplyMessage: !!content.reply_msg,
    isForwardMessage: !!(
      !content.reply_msg &&
      content.reply_to &&
      content.reply_msg_id
    ),
    ...booleans,
    replyedMessage: content.reply_msg,
    replyedMessageId: content.reply_msg_id,
    replyedMessageTo:
      contacts.get(content.reply_to?.split("@")[0] || "")?.name ||
      user.id === content.reply_to?.split("@")[0]
        ? user.name
        : "Participant",
  };
  return msg;
};
export function isUrl(str: string) {
  var expression = /(https?:\/\/(?:www\.|(?!www))[a-zA-Z0-9][a-zA-Z0-9-]+[a-zA-Z0-9]\.[^\s]{2,}|www\.[a-zA-Z0-9][a-zA-Z0-9-]+[a-zA-Z0-9]\.[^\s]{2,}|https?:\/\/(?:www\.|(?!www))[a-zA-Z0-9]+\.[^\s]{2,}|www\.[a-zA-Z0-9]+\.[^\s]{2,})/gi;
  var regex = new RegExp(expression);
  return !!str?.match(regex);
}
export function getUrlBooleans(msg: string) {
  const splittedMsg = msg.split("/");
  const booleans = {
    isImageMessage: /\.(jpe?g|png|gif|ico)$/i.test(msg),
    isVideoMessage: /\.(mp4|avi)$/i.test(msg),
    isAudioMessage: /\.(ogg|mp3|wav|m4a|webm)$/i.test(msg),
    isFileMessage:
      splittedMsg[splittedMsg.length - 1].includes(".") &&
      splittedMsg.length > 1,
  };
  booleans.isFileMessage =
    booleans.isFileMessage &&
    !booleans.isImageMessage &&
    !booleans.isVideoMessage &&
    !booleans.isAudioMessage;
  return booleans;
}
export function getFileName(msg: string) {
  const splittedMsg = msg.split("/");
  return splittedMsg[splittedMsg.length - 1];
}
export function parseConferences(
  conferences: ConferenceRoom[],
  settings: Settings
) {
  const parsedConferences: IConference[] = conferences.map((conf) => {
    const parsedParcicipants: ConferenceRoomParticipant[] = [];
    conf.conferenceRoomParticipantExternals.forEach((part) => {
      parsedParcicipants.push(part);
    });
    conf.conferenceRoomParticipants.forEach((part) => {
      if (!parsedParcicipants.find((p) => p.userUuid === part.userUuid))
        parsedParcicipants.push(part);
    });
    conf.conferenceRoomModerators.forEach((part) => {
      if (!parsedParcicipants.find((p) => p.userUuid === part.userUuid))
        parsedParcicipants.push(part);
    });
    return {
      id: conf.id,
      jid: `${conf.id}@conference.public-${settings.xmppDomain}`,
      name: conf.name,
      number: conf.number,
      anonymousModeratorUrl: conf.anonymousModeratorUrl,
      anonymousParticipantUrl: conf.anonymousParticipantUrl,
      citrusInvationParticipantExternal: conf.citrusInvationParticipantExternal,
      citrusSendPin: conf.citrusSendPin,
      conferenceRoomParticipants: parsedParcicipants,
      createdBy: conf.createdBy,
      description: conf.description,
      domainName: conf.domainName,
      enabledLiveStream: conf.enabledLiveStream,
      liveStreamUrl: conf.liveStreamUrl,
      meetingUuid: conf.meetingUuid,
      moderatorPin: conf.moderatorPin,
      numberExternal: conf.numberExternal,
      participantPin: conf.participantPin,
      record: conf.record,
      rtmpUrl: conf.rtmpUrl,
      serverType: conf.serverType,
      sfuUrl: conf.sfuUrl,
      startDatetime: conf.startDatetime,
      startDatetimeStr: conf.startDatetimeStr,
      stopDatetime: conf.stopDatetime,
      stopDatetimeStr: conf.stopDatetimeStr,
      userCreatedName: conf.userCreatedName,
      waitMod: conf.waitMod,
    };
  });
  return parsedConferences;
}
