import React from "react";
import {
  Button,
  UncontrolledTooltip,
  Input,
  InputGroup,
  InputGroupAddon,
} from "reactstrap";

import { useModal } from "./../contexts/ModalContext";
import { useTranslation } from "react-i18next";
import ModalAddContacts from "./ModalAddContacts";
import ContactList from "./ContactList";
import { useContact } from "../contexts/ContactContext";

const ContactTab: React.FC = () => {
  const { showModalAddContact, setShowModalAddContact } = useModal();
  const {
    contactsSearched,
    contactsSearch,
    setContactsSearch,
    contacts,
  } = useContact();

  function toggle() {
    setShowModalAddContact(!showModalAddContact);
  }

  const { t } = useTranslation();
  return (
    <React.Fragment>
      <div>
        <div className="p-4">
          <div className="user-chat-nav float-right">
            <div id="add-contact">
              <Button
                type="button"
                color="link"
                onClick={toggle}
                className="text-decoration-none text-muted font-size-18 py-0"
              >
                <i className="ri-user-add-line"></i>
              </Button>
            </div>
            <UncontrolledTooltip target="add-contact" placement="bottom">
              {t("Add Contacts")}
            </UncontrolledTooltip>
          </div>
          <h4 className="mb-4">{t("Contacts")}</h4>

          <div className="search-box chat-search-box">
            <InputGroup size="lg" className="bg-light rounded-lg">
              <InputGroupAddon addonType="prepend">
                <Button
                  color="link"
                  className="text-decoration-none text-muted pr-1"
                  type="button"
                >
                  <i className="ri-search-line search-icon font-size-18"></i>
                </Button>
              </InputGroupAddon>
              <Input
                type="text"
                className="form-control bg-light "
                placeholder={t("Search users..")}
                value={contactsSearch}
                onChange={(e) => setContactsSearch(e.target.value)}
              />
            </InputGroup>
          </div>
        </div>
        <ContactList recentContactList={contactsSearched || contacts} />
      </div>
      <ModalAddContacts />
    </React.Fragment>
  );
};

export default ContactTab;
