import React, { useState, useEffect } from "react";
import SimpleBar from "simplebar-react";
import { useChat } from "../contexts/ChatContext";
import ChatMessage from "./ChatMessage";
import ChatMessageIsTyping from "./ChatMessageIsTyping.tsx";
import { ModalSelectUsers } from './ModalSelectUsers';

const ChatBody: React.FC = () => {
  const { activeChat } = useChat();
  const [showButtonBottom, setShowButtonBottom] = useState(true);
  const [showModal, setShowModal] = useState(false);

  const toggle = () => {
    setShowModal(!showModal);
  };

  const scrolltoBottom = () => {
    setShowButtonBottom(false);
  };

  useEffect(() => console.log(activeChat), [activeChat]);

  return (
    <>
      <SimpleBar
        style={{ maxHeight: "100%" }}
        className="chat-conversation p-3 p-lg-4"
        id="messages"
      >
        <ul className="list-unstyled mb-0">
          {activeChat.messages.map((message, key) => (
            <ChatMessage activeChat={activeChat} message={message} key={key} toggle={toggle}/>
          ))}
          <ChatMessageIsTyping activeChat={activeChat} />
        </ul>
      </SimpleBar>
      <ModalSelectUsers show={showModal} toggle={toggle}/*  activeChat={activeChat} selectedMessage={activeChat.messages.map((message, key) => {

      })}  *//>
      {showButtonBottom && (
        <div className="btn-chat-down" onClick={scrolltoBottom}>
          <i className="ri-arrow-down-s-line"></i>
        </div>
      )}
    </>
  );
};

export default ChatBody;
