import React from "react";
import { Formik, Form as FormFormik, Field, ErrorMessage } from "formik";
import * as yup from "yup";
import { useModal } from "./../contexts/ModalContext";
import { useAuth } from "./../contexts/AuthContext";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  ModalFooter,
} from "reactstrap";

const validations = yup.object().shape({
  name: yup
    .string()
    .required("O campo é obrigatório")
    .min(3, "O campo tem que ter no minimo 3 caracteres"),
  email: yup
    .string()
    .required("O campo é obrigatório")
    .email("O campo tem que ser um e-mail"),
});

interface IValue {
  name: string;
  email: string;
}

const ModalEditInfo: React.FC = () => {
  const { showModalEditInfo, setShowModalEditInfo } = useModal();
  const { setChangedUserData, user } = useAuth();

  const initialValues: IValue = {
    name: user.name,
    email: user.email,
  };

  const onSubmit = (data: IValue) => {
    setChangedUserData({ name: data.name, email: data.email });
    setShowModalEditInfo(false);
  };

  const close = () => setShowModalEditInfo(false);

  return (
    <Modal backdrop="static" isOpen={showModalEditInfo} centered>
      <ModalHeader toggle={close}>Edit personal info</ModalHeader>
      <ModalBody>
        <CardBody>
          <Formik
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validations}
          >
            <FormFormik>
              <div className="form-group">
                <label>Nome</label>
                <Field
                  name="name"
                  className="form-control"
                  placeholder="nome"
                />
                <ErrorMessage
                  component="small"
                  name="name"
                  className="form-text text-danger"
                />
              </div>
              <div className="form-group">
                <label>E-mail</label>
                <Field
                  name="email"
                  className="form-control"
                  placeholder="e-mail"
                />
                <ErrorMessage
                  component="small"
                  name="email"
                  className="form-text text-danger"
                />
              </div>
              <ModalFooter className="border-0">
                <Button color="default" onClick={close}>
                  Close
                </Button>
                <Button type="submit" color="primary">
                  Confirm
                </Button>
              </ModalFooter>
            </FormFormik>
          </Formik>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalEditInfo;
