import React, { createContext, useContext, useState } from "react";
import {
  activeChat as activeChatData,
  chats as chatsData,
  groups as groupsData,
  contacts as contactsData,
} from "./../data/chat";

//alias
import IChat from "../alias/IChat";
import IGroup from "./../alias/IGroup";
import IContact from "./../alias/IContact";

interface IChatContextData {
  showChat: boolean;
  setShowChat(value: boolean): void;
  activeChat: IChat;
  setActiveChat(value: IChat): void;
  openChat(obj: IChat): void;
  openGroup(obj: IGroup): void;
  chats: IChat[];
  setChats(value: IChat[]): void;
  groups: IGroup[];
  setGroups(value: IGroup[]): void;
  contacts: IContact[];
  setContacts(value: IContact[]): void;
}

export const ChatContext = createContext<IChatContextData>(
  {} as IChatContextData
);

export const ChatProvider: React.FC = ({ children }) => {
  const [showChat, setShowChat] = useState(false);
  const [activeChat, setActiveChat] = useState(activeChatData);
  const [chats, setChats] = useState<IChat[]>(chatsData);
  const [groups, setGroups] = useState<IGroup[]>(groupsData);
  const [contacts, setContacts] = useState<IContact[]>(contactsData);

  const openChat = (obj: IChat) => {
    setChats(
      chats.map((item) => {
        if (item.id !== obj.id) {
          setActiveChat(obj);
          return item;
        } else {
          item.unRead = 0;
          return item;
        }
      })
    );
  };

  const openGroup = (obj: IGroup) => {
    setChats(
      chats.map((item) => {
        if (item.groupId !== obj.groupId) {
          setActiveChat(item);
          return item;
        } else {
          item.unRead = 0;
          return item;
        }
      })
    );
  };

  return (
    <ChatContext.Provider
      value={{
        showChat,
        setShowChat,
        activeChat,
        setActiveChat,
        openChat,
        openGroup,
        chats,
        setChats,
        groups,
        setGroups,
        contacts,
        setContacts,
      }}
    >
      {children}
    </ChatContext.Provider>
  );
};

export function useChat() {
  return useContext(ChatContext);
}
