import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  ModalFooter,
} from "reactstrap";

import { useModal } from "./../contexts/ModalContext";

import SimpleBar from "simplebar-react";
import IContact from "../alias/IContact";
import SelectContact from "./SelectContact";
import { useChat } from "../contexts/ChatContext";
import SelectGroup from "./SelectGroup";
import IGroup from "../alias/IGroup";

const ModalForwardMessageTo: React.FC = () => {
  const {
    showModalForwardMessageTo,
    setShowModalForwardMessageTo,
  } = useModal();
  const { setForwardMessageList } = useChat();
  const toggle = () => {
    if (showModalForwardMessageTo) {
      setShowModalForwardMessageTo(undefined);
    }
  };

  const [contacts, setContacts] = useState<IContact[]>([]);
  const [groups, setGroups] = useState<IGroup[]>([]);

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    let data = [...contacts];
    if (selected) {
      data.push(contact);
    } else {
      data = data.filter((item) => {
        if (item.id === contact.id) {
          return false;
        }
        return true;
      });
    }
    setContacts(data);
  };

  const handleCheckGroup = (selected: boolean, group: IGroup) => {
    let data = [...groups];
    if (selected) {
      data.push(group);
    } else {
      data = data.filter((item) => {
        if (item.jid === group.jid) {
          return false;
        }
        return true;
      });
    }
    setGroups(data);
  };

  const sendMessages = () => {
    setForwardMessageList(contacts);
  };

  return (
    <Modal
      backdrop="static"
      isOpen={!!showModalForwardMessageTo}
      centered
      toggle={toggle}
    >
      <ModalHeader toggle={toggle}>Forward to...</ModalHeader>
      <ModalBody>
        <CardBody className="p-2 border">
          <div className="pt-2 pb-3 border-bottom">
            <SimpleBar style={{ maxHeight: "200px" }}>
              <h5 className="ml-3">Contatos</h5>
              <SelectContact handleCheckContact={handleCheckContact} />
              <hr />
              <h5 className="ml-3">Grupos</h5>
              <SelectGroup handleCheckGroup={handleCheckGroup} />
            </SimpleBar>
          </div>
          <ModalFooter className="border-0">
            <Button color="default" onClick={toggle}>
              Close
            </Button>
            <Button color="primary" onClick={sendMessages}>
              Forward
            </Button>
          </ModalFooter>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalForwardMessageTo;
