import React from "react";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import { DateTime } from "luxon";

import IMessage from "../alias/IMessage";
import IUser from "../alias/IUser";

interface IProps {
  message: IMessage;
  user: IUser;
}

const ChatMessageFooter: React.FC<IProps> = ({ message, user }) => {
  return (
    <p className="chat-time mb-0">
      <i
        style={{
          position: "relative",
          right: 8,
          top: 1,
          color: "rgb(114, 105, 239)",
        }}
        className={
          message.deliverStatus === DeliverStatus.SENDING &&
          user.id === message.fromUser
            ? "ri-time-line align-middle"
            : ""
        }
      ></i>
      <i
        style={{
          position: "relative",
          right: 8,
          top: 1,
          color: "rgb(114, 105, 239)",
        }}
        className={
          message.deliverStatus === DeliverStatus.QUEUED &&
          message.readStatus === ReadStatus.UNREAD &&
          user.id === message.fromUser
            ? "ri-check-line align-middle"
            : ""
        }
      ></i>
      <i
        style={{
          position: "relative",
          right: 8,
          top: 1,
          color: "rgb(114, 105, 239)",
        }}
        className={
          message.deliverStatus === DeliverStatus.DELIVERED &&
          message.readStatus === ReadStatus.UNREAD &&
          user.id === message.fromUser
            ? "ri-check-double-line align-middle"
            : ""
        }
      ></i>
      <i
        style={{
          position: "relative",
          right: 8,
          top: 1,
          color: "rgb(35, 198, 10)",
        }}
        className={
          message.readStatus === ReadStatus.READ && user.id === message.fromUser
            ? "ri-check-double-line align-middle"
            : ""
        }
      ></i>
      <span className="align-middle" style={{ position: "relative", right: 4 }}>
        {message.time === "" || message.time === "—:—"
          ? "—:—"
          : message.time !== ("" && "—:—") &&
            DateTime.fromISO(message.time).day === DateTime.local().day
          ? DateTime.fromISO(message.time).setLocale("pt").toLocaleString(DateTime.TIME_SIMPLE)
          : DateTime.fromISO(message.time).toLocaleString(
              DateTime.DATETIME_SHORT
            )}
      </span>
    </p>
  );
};

export default ChatMessageFooter;
