import React, { useState, useEffect } from "react";
import {
  Button,
  Input,
  Row,
  Col,
  UncontrolledTooltip,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  Label,
} from "reactstrap";
import { Picker, Emoji } from "emoji-mart";
import "emoji-mart/css/emoji-mart.css";
import { ReactMic } from "react-mic";

import ChatInputAttachedFileButton from "./ChatInputAttachedFileButton";
//import ChatInputImagesButton from "./ChatInputImagesButton";
import { useChat } from "../contexts/ChatContext";
import IChat from "../alias/IChat";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import { useAuth } from "../contexts/AuthContext";

const ChatInput: React.FC = () => {
  const { chats, setChats, activeChat } = useChat();
  const { user } = useAuth();
  const [activeChatCurrent, setActiveChatCurrent] = useState<IChat | null>(
    null
  );
  const [textMessage, setTextMessage] = useState("");
  const [isOpen, setisOpen] = useState(false);
  const [file, setFile] = useState<File | null>(null);
  const [record, setRecord] = useState(false);
  const [deleteAudio, setDeleteAudio] = useState(false);

  const init = () => {
    setChats(
      chats.map((item) => {
        if (activeChatCurrent)
          if (item.id === activeChatCurrent?.id) {
            item.inputMessage = textMessage;
            setActiveChatCurrent(null);
          }
        return item;
      })
    );
    chats.forEach((element) => {
      if (element.id === activeChat.id) {
        setTextMessage(element.inputMessage);
        setActiveChatCurrent(activeChat);
      }
    });
  };

  useEffect(init, [activeChat]);

  const toggle = () => setisOpen(!isOpen);

  const handleChange = (e: any) => {
    setTextMessage(e.target.value);
  };

  const addEmoji = (e: any) => {
    let emoji = e.native;
    setTextMessage(textMessage + emoji);
  };

  const onData = (recordedBlob: any) => {
    console.log("chunk of real-time data is: ", recordedBlob);
  };

  const onStop = (recordedBlob: any) => {
    if (!deleteAudio) {
      console.log("recordedBlob is: ", recordedBlob);
    } else {
      setDeleteAudio(false);
    }
  };

  const onKeyPress = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.which === 13) {
      onaddMessage();
    }
  };

  const onaddMessage = (file: File | undefined = undefined) => {
    if (textMessage !== "" || file) {
      console.log(file);
      setChats(
        chats.map((item) => {
          if (activeChat)
            if (item.id === activeChat?.id) {
              item.messages.push({
                id: `${
                  item.messages ? item.messages[item.messages.length - 1] : 1
                }`,
                userName: user.name,
                message: file ? "Carregando..." : textMessage,
                time: "",
                isImageMessage: false,
                isFileMessage: false,
                isVideoMessage: false,
                isAudioMessage: false,
                fileUrl: "",
                sendFile: file,
                toUser: activeChat.id,
                fromUser: user.id,
                deliverStatus: DeliverStatus.SENDING,
                readStatus: ReadStatus.UNREAD,
                deliverTimestamp: "",
                readTimestamp: "",
                content: "metadata",
                contentType: "type",
              });
            }
          return item;
        })
      );
      setFile(null);
      setTextMessage("");
    }
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>) => {
    let files = e.clipboardData.files;
    console.log(e.clipboardData.getData("Text"));

    if (files.length === 0) return;
    let file = files[0];
    if (file.size > 15000) {
      alert(
        "Tamanho de arquivo " +
          file.name +
          " não suportado. Por favor, envie um arquivo menor do que 15mb"
      );
      return;
    }

    //setFile(file);
    console.log("arquivo enviado");
  };

  const loadFiles = () => {
    if (file) {
      onaddMessage(file);
    }
  };

  useEffect(loadFiles, [file]);

  return (
    <React.Fragment>
      <div className="p-3 p-lg-4 border-top mb-0">
        <div>
          <Row noGutters>
            <Col>
              <div>
                <ReactMic
                  record={record}
                  className={`sound-wave ${record ? "" : "d-none"}`}
                  onStop={onStop}
                  onData={onData}
                  strokeColor="#7a7f9a"
                  backgroundColor="#e6ebf5"
                />
                <Input
                  type="text"
                  value={textMessage}
                  onKeyPress={onKeyPress}
                  onChange={handleChange}
                  onPaste={handlePaste}
                  className={`form-control form-control-lg bg-light border-light ${
                    record ? "d-none" : ""
                  }`}
                  placeholder={"Send message..."}
                />
                <div
                  style={{
                    color: "red",
                    fontSize: 12,
                  }}
                >
                  {textMessage.length > 9900
                    ? 10000 - textMessage.length + " caracteres restantes"
                    : null}
                </div>
              </div>
            </Col>
            <Col xs="auto">
              <div className="chat-input-links ml-md-2">
                <ul className="list-inline mb-0">
                  {!record ? (
                    <li className="list-inline-item">
                      <ButtonDropdown
                        className="emoji-dropdown"
                        direction="up"
                        isOpen={isOpen}
                        toggle={toggle}
                      >
                        <DropdownToggle
                          id="emoji"
                          color="link"
                          className="text-decoration-none font-size-16 btn-lg waves-effect"
                        >
                          <i className="ri-emotion-happy-line"></i>
                        </DropdownToggle>
                        <DropdownMenu className="dropdown-menu-lg-right">
                          <Picker set="apple" onSelect={addEmoji} />
                          <Emoji emoji="santa" set="apple" size={16} />
                        </DropdownMenu>
                      </ButtonDropdown>
                      <UncontrolledTooltip target="emoji" placement="top">
                        Emoji
                      </UncontrolledTooltip>
                    </li>
                  ) : (
                    <li className="list-inline-item input-file">
                      <Label
                        id="audio"
                        className={`btn btn-link text-decoration-none font-size-16 btn-lg waves-effect`}
                        onClick={() => {
                          setDeleteAudio(true);
                          setRecord(false);
                        }}
                      >
                        <i className="ri-delete-bin-5-line" />
                      </Label>
                      <UncontrolledTooltip target="audio" placement="top">
                        Áudio
                      </UncontrolledTooltip>
                    </li>
                  )}
                  <li className="list-inline-item input-file">
                    <Label
                      id="audio"
                      className={`btn btn-link text-decoration-none font-size-16 btn-lg waves-effect ${
                        record ? "text-record" : ""
                      }`}
                      onClick={() => setRecord(!record)}
                    >
                      {record ? (
                        <i className="ri-stop-circle-line" />
                      ) : (
                        <i className="ri-mic-line" />
                      )}
                    </Label>
                    <UncontrolledTooltip target="audio" placement="top">
                      Áudio
                    </UncontrolledTooltip>
                  </li>

                  <ChatInputAttachedFileButton setFile={setFile} />

                  <li className="list-inline-item">
                    <Button
                      type={textMessage.length < 10000 ? "submit" : "button"}
                      color="primary"
                      className="font-size-16 btn-lg chat-send waves-effect waves-light"
                      onClick={() => onaddMessage()}
                    >
                      <i className="ri-send-plane-2-fill"></i>
                    </Button>
                  </li>
                </ul>
              </div>
            </Col>
          </Row>
        </div>
      </div>
    </React.Fragment>
  );
};

export default ChatInput;
