import React from 'react';
import { useTranslation } from 'react-i18next';
import IMessage from '../alias/IMessage';
import { useContact } from '../contexts/ContactContext';
// eslint-disable-next-line no-lone-blocks
{/* 
<div className="chat-day-title">
    <span className="title">Evento chegou!</span>
</div> 
*/}

interface IGroupNotification{
    msg: IMessage;
}

const EventNotification: React.FC<IGroupNotification> = ({ msg }) => {
    let eventSender = msg.userName;
    const { contacts, contactsOutsideUserContacts } = useContact();
    const { t } = useTranslation();
    switch(msg.eventId){
        case 1:
            return (
            <div className="chat-day-title">
                <span className="title">{t("Group has been created!")}</span>
            </div>
            ); 
        case 2:
        let userThatIsBeingAdded: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingAdded = ctc.name;
            };
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingAdded = ctc.name;
            };
        })
        return(
            <div className="chat-day-title">
                <span className="title">{userThatIsBeingAdded || msg.eventBody || t("User")} {t("has been added!")}</span>
             </div> 
        );
        case 3:
        let userThatIsLeaving: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsLeaving = ctc.name;
            };
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsLeaving = ctc.name;
            };
        })
        return(
            <div className="chat-day-title">
                <span className="title">{userThatIsLeaving || msg.eventBody || eventSender || t("User")} {t("has left the group!")}</span>
            </div>
        );
        case 6:
        let userThatIsBeingRemoved: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingRemoved = ctc.name;
            };
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingRemoved = ctc.name;
            };
        })
        return(
            <div className="chat-day-title">
                <span className="title">{userThatIsBeingRemoved || msg.eventBody || t("User")} {t("has been removed!")}</span>
            </div>
        );
        case 8: 
        let userTurningAdmin: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userTurningAdmin = ctc.name;
            };
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userTurningAdmin = ctc.name;
            };
        })
        return(
            <div className="chat-day-title">
                <span className="title">{t("User")} {userTurningAdmin} {t("is now an admin!")}</span>
            </div>
        )
        case 9: 
        let userTurningMember: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userTurningMember = ctc.name;
            };
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userTurningMember = ctc.name;
            };
        })
        return (
            <div className="chat-day-title">
                <span className="title">{t("User")} {userTurningMember} {t("is now a member!")}</span>
            </div>
        )
        case 10:
            return(
            <div className="chat-day-title">
                <span className="title">{t("Group info has been edited!")}</span>
            </div>
            );
        default:
            return <></>    
    }
}

export default EventNotification;
