import API from "@digivox/apicommunicator";
import IGroup from "../../alias/IGroup";
import INewGroup from "../../alias/INewGroup";

import { parseGroup } from "../utils/parseUtils";
import SettingsBusiness from "./Settings";

export interface NewGroupCallback {
  (group: IGroup): void;
}
export interface GroupsCallBack {
  (groups: IGroup[]): void;
}
export default interface GroupsBusiness {
  getGroups(): Map<string, IGroup>;
  list(callback: GroupsCallBack): void;
  create(group: INewGroup, cb: NewGroupCallback): void;
}
export default class GroupsBusiness {
  public groups: Map<string, IGroup>;
  constructor(private api: API, private settings: SettingsBusiness) {
    this.groups = new Map<string, IGroup>();
  }
  getGroups = () => {
    return this.groups;
  };
  list = (callback: GroupsCallBack) => {
    this.api.getGroups(
      { paramsUrl: { members: true } },
      (apiGroups: any, err) => {
        // Populando o Map de grupos
        if (!err) {
          if (!this.api) return;
          this.populate(apiGroups);
          callback(Array.from(this.groups.values()));
        }
      }
    );
  };
  create = (group: INewGroup, callback: NewGroupCallback) => {
    if (!this.api) return;
    this.settings.get((settings) => {
      const contactsId = group.contacts.map((contact) => contact.id);
      const body = {
        name: group.name,
        description: group.description,
        members: contactsId,
        domain: settings.sipDomain,
      };
      this.api.createGroup({ body }, (returnGroup, err) => {
        if (err) return;
        if (!this.api) return;
        if (!returnGroup) return;
        this.api.getGroup(
          returnGroup.id,
          { paramsUrl: { members: true } },
          (groupWithMember) => {
            const parsedGroup = parseGroup(groupWithMember);
            this.groups.set(parsedGroup.groupId, parsedGroup);
            callback(parsedGroup);
          }
        );
      });
    });
  };
  populate(apiGroups: any) {
    apiGroups.groups.forEach((group: any) => {
      this.groups.set(group.id, parseGroup(group));
    });
  }
}
