import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { Button } from 'reactstrap';
import IChat from '../alias/IChat';
import IMember from '../alias/IMember';
import { useGroup } from '../contexts/GroupContext';
import { toastSuccess } from '../helpers/toastCaller';
import ModalGenerics from './ModalGenerics';
interface IProps{
    activeChat: IChat;
    actualMember: IMember | undefined;
}

const LeaveGroupButton : React.FC<IProps> = ({ activeChat, actualMember }) => {
    const { t } = useTranslation();
    const { setGroupIdOfLeaveRequest, setGroupJid } = useGroup();
    const { setMemberQuit } = useGroup();
    const [show, setShow] = useState<boolean>(false);
    return <div className="px-3 px-lg-4 pt-3 pt-lg-4">
    <div className="user-chat-nav text-right">
    <ModalGenerics
      setShow={setShow}
      show={show}
      title={t("Leave the group?")}
      children={
        <>
          <Button type="button" color="link" onClick={() => setShow(!show)}>
              {t("Cancel")}
          </Button>
          <Button type="button" color="primary" 
          onClick={() => {
            activeChat.groupId &&
            setGroupJid(activeChat.jid);
            setGroupIdOfLeaveRequest(activeChat.groupId as string);
            setMemberQuit(actualMember);
            setShow(!show)
          }}>
            {t("Confirm")}
          </Button>
        </>
      }
      />
      <Button
        color="none"
        type="button"
        onClick={() =>
          setShow(true)
        }
        className="nav-btn"
        id="user-profile-hide"
      >
        <i className="ri-logout-box-line"></i>
      </Button>
    </div>
  </div>
}

export default LeaveGroupButton;
