import React, { useEffect } from "react";
import { SquadCommunicator as SquadService } from "./SquadCommunicatorService";
import { useChat } from "../contexts/ChatContext";
import { useAuth } from "../contexts/AuthContext";
import IUser from "../alias/IUser";
import { Events, SendImageCallbackReturn } from "./chat/types/types";
import ChatType from "./../enuns/ChatType";
import IChat from "../alias/IChat";
import DeliverStatus from "../enuns/DeliverStatus";
import { ChatType as XMPPChatType } from "./chat/types/types";
import { SendMessageCallback } from "./types";
import IMessage from "../alias/IMessage";
import { getFileName, getUrlBooleans, isUrl } from "./utils/parseUtils";
import ReadStatus from "../enuns/ReadStatus";
import { useGroup } from "../contexts/GroupContext";
import { useContact } from "../contexts/ContactContext";
import { useModal } from "../contexts/ModalContext";
import IContact from "../alias/IContact";
import IGroup from "../alias/IGroup";

//import { callbackify } from "util";

// import IContact from "../alias/IContact";
 //Comentar para Evitar Warnings no console
 interface CreateGroupCallback {
   (success: boolean): void;
 }
interface TypingTimeoutData {
  timeout: NodeJS.Timeout;
  msgStr: string;
}
let squadService: SquadService | undefined;
let typingTimeout: Map<string, TypingTimeoutData> = new Map<
  string,
  TypingTimeoutData
>();

const SquadCommunicator: React.FC = () => {
  const {
    chats,
    setChats,
    activeChat,
    replyMsg,
    setReplyMsg,
    setActiveChat,
    forwardMessageList,
    setForwardMessageList,
    setShowChat,
  } = useChat();
  const {
    contactsSearch,
    setContactsSearched,
    newFavorite,
    setNewFavorite,
    contacts,
    setContacts,
    setContactsOutsideUserContacts,
    toggleAddingContacts,
    setToggleAddingContacts,
    checkedContacts,
    setCheckedContacts,
    removedContact,
    setRemovedContact,
    checkedMembers,
    groupToInsertMember,
    setGroupToInsertMember,
    clickedContact,
    setClickedContact
  } = useContact();
  const {
    user,
    setUser,
    changedUserData,
    changedUserPassword,
    setChangedUserPassword,
  } = useAuth();
  const {
    showModalForwardMessageTo,
    setShowModalForwardMessageTo,
    showModalAddContact,
  } = useModal();

  const {
    setGroups,
    groups,
    memberQuit,
    groupIdOfLeaveRequest,
    setGroupIdOfLeaveRequest,
    setMemberQuit,
    groupToRemoveContact,
    setGroupToRemoveContact,
    removedMember,
    setRemovedMember,
    toggleMemberRole,
    setToggleMemberRole,
    changeGroupData,
    setChangeGroupData,
    groupToCreate,
    setGroupToCreate
  } = useGroup();

  const init = () => {
    /* localStorage.setItem(
      "authorization",
      JSON.stringify({
        access_token: "71e1503a96d94e1e231444bc5c0399ca",
        user_id: "11d3a709-8c53-0392-0149-3a3a0e0eeaae",
        user_name: "ZZ Romero 2",
        expires_in: 2592000,
        email: "romero2@digivox.com.br",
        baseUrl: "https://app.citrussquad.com/api/v1/",
      })
    ); */
    squadService = SquadService.getInstance({
      chatCommunicatorSubscribe: chatCommunicatorSubscribe,
    });
    squadService?.user.get((user: IUser) => {
      setUser(user);
    });
  };

  const clearSendFile = (chatId: string, messageId: string) => {
    setChats(
      chats.map((chat) => {
        if (chat.id === chatId) {
          chat.messages = chat.messages.map((message) => {
            if (message.id === messageId) {
              message.deliverStatus = DeliverStatus.QUEUED;
              message.sendFile = undefined;
            }
            return message;
          });
        }
        return chat;
      })
    );
  };

  const onChatsChange = () => {
    // chats.forEach((chat) => {
    // });
  };
  const onActiveChatChange = () => {
    checkSendMessage(activeChat);
    checkAttachedFiles(activeChat);
    checkNewContact(activeChat);
    // checkTyping(activeChat)
  };
  // const checkTyping = (chat: IChat) => {
  //   const oldTimeoutData = typingTimeout.get(chat.jid)
  //   if (chat.inputMessage !== "" && (!oldTimeoutData || (oldTimeoutData.msgStr !== chat.inputMessage))) {
  //     if (oldTimeoutData)
  //       clearTimeout(oldTimeoutData.timeout)
  //     else
  //       squadService?.chat?.chat.sendTyping(chat.jid)
  //     const timeoutData = {
  //       timeout: setTimeout(() => { squadService?.chat?.chat.sendActive(chat.jid); typingTimeout.delete(chat.jid) }, 3000),
  //       msgStr: `${chat.inputMessage}`
  //     }
  //     typingTimeout.set(chat.jid, timeoutData)
  //   }
  // }
  const checkNewContact = (cht: IChat) => {
    if (!cht.added) {
      squadService?.contacts.add(cht, (success: boolean) => {
        if (success) {
          setChats(
            chats.map((chat) => {
              if (chat.jid === cht.jid) {
                chat.added = true;
              }
              if (activeChat.jid === chat.jid) setActiveChat(chat);
              return chat;
            })
          );
          squadService?.contacts.list((ctcs) => {
            setContacts(ctcs);
          });
        }
      });
    }
  };

  const checkAttachedFiles = (cht: IChat) => {
    if (cht.attachedFileList && !cht.attachedFileList[0]) {
      squadService?.attachments.list(cht, (attachments) => {
        if (attachments.length > 0)
          setActiveChat({ ...cht, attachedFileList: attachments });
        else setActiveChat({ ...cht, attachedFileList: null });
      });
    }
  };

  /* getMessageUserProfilePicture = (msg: Message | FileMessage) => {
    if (msg.from.includes("@conference.")) {
      const groups = this.groups.getGroups();
      const group = groups.get(msg.from.split("@")[0]);
      const member = group?.members.filter(
        (member) => member.userId === msg.from.split("/")[1]
      )[0];
      return member?.profilePicture;
    } else {
      return this.contacts.getContacts().get(msg.from.split("@")[0])
        ?.profilePicture;
    }
  }; */

  const checkSearchMessages = () => {
    if (activeChat.messageLoaded) {
      squadService?.messages.list(activeChat, (messages) => {
        if (messages.length) {
          setChats(
            chats.map((cht) => {
              if (cht.id === activeChat.id) {
                messages.forEach((msg) => {
                  if (!activeChat.messages.find((m) => m.id === msg.id))
                    cht.messages.unshift(msg);
                });
                cht.messageLoaded = false;
                setActiveChat(cht);
              }
              return cht;
            })
          );
        } else {
          const chat = { ...activeChat, messageLoaded: false };
          setActiveChat(chat);
        }
      });
    }
  };

  const checkSendMessage = (chat: IChat) => {
    chat.messages.forEach((msg) => {
      if (msg.deliverStatus === DeliverStatus.SENDING) {
        if (msg.sendFile && !replyMsg) {
          const files = [msg.sendFile];
          clearSendFile(chat.id, msg.id);
          squadService?.chat?.sendFiles(
            chat,
            files,
            (data: SendImageCallbackReturn) => {
              updateMessageFiles(data, chat, msg);
            }
          );
        } else {
          if (!msg.isForwardMessage && !replyMsg) {
            sendMessage(chat, msg.message, (msgId) =>
              updateMessage(msgId, chat, msg)
            );
          } else if (replyMsg) {
            const replyMsgCopy = { ...replyMsg };
            setReplyMsg(undefined);
            squadService?.chat?.replyMsg(chat, msg, replyMsgCopy, (msgId) => {
              updateMessage(msgId, chat, msg, replyMsgCopy);
            });
          }
        }
      }
    });
  };
  const updateMessageFiles = (
    data: SendImageCallbackReturn,
    chat: IChat,
    msg: IMessage
  ) => {
    const newChat = { ...chat };
    newChat.messages = chat.messages.map((message) => {
      if (msg.id === message.id) {
        const booleans = getUrlBooleans(data.url);
        message.id = data.msgId;
        message.fileUrl = data.url;
        message.deliverStatus = DeliverStatus.QUEUED;
        message.time = new Date().toISOString();
        message.message = getFileName(data.url);
        message.isFileMessage = booleans.isFileMessage;
        message.isImageMessage = booleans.isImageMessage;
        message.isVideoMessage = booleans.isVideoMessage;
        message.isAudioMessage = booleans.isAudioMessage;
      }
      return message;
    });
    setActiveChat(newChat);
    if (!chats.filter((cht) => cht.id === chat.id)[0]) {
      const newChats = [chat, ...chats];
      setChats(newChats);
    } else {
      const newChats: IChat[] = [];
      chats.forEach((cht) => {
        if (cht.id === chat.id) {
          cht.messages = newChat.messages;
          newChats.unshift(cht);
        } else {
          newChats.push(cht);
        }
      });
      setChats(newChats);
    }
  };
  const updateMessage = (
    msgId: string,
    chat: IChat,
    msg: IMessage,
    replyedMsg: IMessage | undefined = undefined
  ) => {
    const newChat = { ...chat };
    newChat.messages = chat.messages.map((message) => {
      if (msg.id === message.id) {
        message.id = msgId;
        message.deliverStatus = DeliverStatus.QUEUED;
        message.time = new Date().toISOString();
        if (replyedMsg) {
          message.isReplyMessage = true;
          message.replyedMessage = replyedMsg.message || replyedMsg.fileUrl;
          message.replyedMessageId = replyedMsg.id;
          message.replyedMessageTo =
            squadService?.contacts
              .getContacts()
              .get(replyedMsg.fromUser.split("@")[0])?.name ||
            squadService?.user?.user?.id === replyedMsg.fromUser.split("@")[0]
              ? squadService?.user?.user?.name
              : "Participant";
        }
        if (isUrl(message.message)) {
          const booleans = getUrlBooleans(message.message);
          message.isFileMessage = booleans.isFileMessage;
          message.isImageMessage = booleans.isImageMessage;
          message.isAudioMessage = booleans.isAudioMessage;
          message.isVideoMessage = booleans.isVideoMessage;
          message.fileUrl = message.message;
        }
      }
      return message;
    });
    if (activeChat.jid === newChat.jid) setActiveChat(newChat);
    if (!chats.filter((cht) => cht.id === chat.id)[0]) {
      const newChats = [chat, ...chats];
      setChats(newChats);
    } else {
      const newChats: IChat[] = [];
      chats.forEach((cht) => {
        if (cht.id === chat.id) {
          cht.messages = newChat.messages;
          newChats.unshift(cht);
        } else {
          newChats.push(cht);
        }
      });
      setChats(newChats);
    }
  };
  const forwardMessage = () => {
    if (forwardMessageList && showModalForwardMessageTo) {
      const ctcs = [...forwardMessageList];
      const message = { ...showModalForwardMessageTo };
      setForwardMessageList(undefined);
      setShowModalForwardMessageTo(undefined);
      const chatsFinded: IChat[] = [];
      const oldChats = [
        ...chats.map((chat) => {
          return { ...chat };
        }),
      ];
      ctcs?.forEach((ctc, i) => {
        const cht = oldChats.find((cht) => cht.jid === ctc.jid);
        if (cht) {
          const msg = {
            ...message,
            id: `${cht.messages.length + 1}`,
            userName: user.name,
            fromUser: user.id,
            toUser: ctc.jid.split("@")[0],
            message: message.fileUrl || message.message,
            deliverStatus: DeliverStatus.QUEUED,
            isForwardMessage: true,
          };
          squadService?.chat?.forwardMessage(cht, msg, (msgId) => {
            msg.id = msgId;
            cht.messages.push(msg);
            chatsFinded.push(cht);
          });
        }
      });
      const newChats = oldChats.map(
        (chat) => chatsFinded.find((cht) => cht.id === chat.id) || chat
      );
      const ctcsNotInChat: (IContact | IGroup)[] = ctcs.filter(
        (ctc) => !newChats.find((cht) => ctc.jid === cht.jid)
      );
      ctcsNotInChat.forEach((ctc, i) => {
        const chat = {
          id: ctc.jid.split("@")[0],
          jid: ctc.jid,
          name: ctc.name,
          profilePicture: ctc.profilePicture,
          status: (ctc as IContact).status || undefined,
          unRead: 0,
          chatType: ctc.jid.includes("@conference.")
            ? ChatType.GROUP
            : ChatType.USER,
          inputMessage: "",
          messageLoaded: false,
          isTyping: false,
          messages: [] as IMessage[],
          email: (ctc as IContact).email || undefined,
          favorite: ctc.favorite,
          added: ctc.added,
        };
        const newMessage: IMessage = {
          ...message,
          id: `${1}`,
          userName: user.name,
          fromUser: user.id,
          toUser: ctc.jid.split("@")[0],
          deliverStatus: DeliverStatus.QUEUED,
          isForwardMessage: true,
        };
        squadService?.chat?.forwardMessage(chat, newMessage, (msgId) => {
          newMessage.id = msgId;
          chat.messages.push(newMessage);
          newChats.unshift(chat);
        });
      });
      setChats(newChats);
    }
  };
  const onChangeStatus = () => {
    if (squadService?.chat?.chat?.client?.status === "online") {
      squadService?.chat?.changeStatus(user.status);
    }
  };
  const onSearchContacts = () => {
    if (contactsSearch.length > 2) {
      squadService?.contacts.search(contactsSearch, (searchList) => {
        setContactsSearched(searchList);
      });
    } else {
      setContactsSearched(undefined);
    }
  };

  const onSearchContactsOutsideUserContacts = () => {
    squadService?.contacts.getContactsOutsideUserLists((contacts) => {
      if (contacts) {
        setContactsOutsideUserContacts(contacts);
      }
    });
  };

  const onRemovingGroupMember = () => {
    if (removedMember && groupToRemoveContact) {
      let group = groups.find((grp) => grp.groupId === groupToRemoveContact);
      if (group)
        squadService?.groups.removeMember(
          group.groupId,
          removedMember,
          (success: boolean) => {
            if (success) {
              squadService?.groups.list((grps) => {
                grps.map((grp) => {
                  grp.members = grp.members.filter(
                    (mb) => mb.userId !== removedMember.userId
                  );
                  setGroups(grps);
                  setRemovedMember(undefined);
                  setGroupToRemoveContact(undefined);
                  return grp;
                });
              });
            }
          }
        );
    }
  };

  const onLeavingGroup = () => {
    if (memberQuit && groupIdOfLeaveRequest) {
      squadService?.groups.leftingGroup(
        { groupId: groupIdOfLeaveRequest, memberId: memberQuit.userId },
        (success: boolean) => {
          if (success) {
            setChats(chats.filter((cht) => 
              cht.groupId !== groupIdOfLeaveRequest)
            );
            setGroups(groups.filter((grp) => 
              grp.groupId !== groupIdOfLeaveRequest
            ));
            if (activeChat.groupId === groupIdOfLeaveRequest) {
              setShowChat(false);
            }
            setMemberQuit(undefined);
            setGroupIdOfLeaveRequest("");
        };
      });
    };
  };

  const onAddingMembers = () => {
    let asynchronousFlag = 0;
    if (checkedMembers && groupToInsertMember) {
      checkedMembers.forEach((ctt) => {
        squadService?.groups.addMember(
          groupToInsertMember,
          ctt,
          (success: boolean) => {
            if (success) {
              asynchronousFlag++;
              if (checkedMembers.length === asynchronousFlag) {
                squadService?.groups.list((grps) => {
                  setGroups(grps);
                  setChats(
                    chats.map((cht) => {
                      if (cht.groupId === groupToInsertMember.groupId) {
                        let foundedGroup = grps.find(
                          (grp) => grp.groupId === cht.groupId
                        );
                        cht.members = foundedGroup?.members;
                        if (activeChat.groupId === cht.groupId) {
                          activeChat.members = foundedGroup?.members;
                          setActiveChat(activeChat);
                        }
                      }
                      return cht;
                    })
                  );
                });
                setCheckedContacts([]);
                setToggleAddingContacts(false);
                setGroupToInsertMember(undefined);
              }
            }
          }
        );
      });
    }
  };

  const onAddingContacts = () => {
    let asynchronousFlag = 0;
    if (checkedContacts && toggleAddingContacts) {
      checkedContacts.forEach((ctt) => {
        if (!ctt.added) {
          squadService?.contacts.addContacts(ctt, (success: boolean) => {
            if (success) {
              asynchronousFlag++;
              if (checkedContacts.length === asynchronousFlag) {
                squadService?.contacts.list((contacts) => {
                  setContacts(contacts);
                  setCheckedContacts([]);
                  setToggleAddingContacts(false);
                });
              }
            }
          });
        }
      });
    }
  };

  const onAddingContactOnGroupList = () => {
    if (clickedContact) {
      if (!clickedContact.added) {
        squadService?.contacts.addContacts(clickedContact, (success: boolean) => {
          if (success) {
            squadService?.contacts.list((contacts) => {
              setContacts(contacts);
              setClickedContact(undefined);
            });
          }
        });
      }
    }
  };


  const onToggleMemberRole = () => {
    if (toggleMemberRole) {
      squadService?.groups.list((grps) => {
        grps.forEach((gp) => {
          if (gp.groupId === toggleMemberRole.gpId) {
            gp.members.forEach((mb) => {
              if (mb.userId === toggleMemberRole.mbId) {
                squadService?.contacts.toggleMemberRole(
                  toggleMemberRole.mbId,
                  toggleMemberRole.gpId,
                  toggleMemberRole.mbRole,
                  (success: boolean) => {
                    if (success) {
                      setToggleMemberRole(undefined);
                      setChats(chats.map((cht) => {
                        if(cht.groupId === toggleMemberRole.gpId){
                          cht.members?.map((mb) => {
                            if(mb.userId === toggleMemberRole.mbId){
                              mb.role = toggleMemberRole.mbRole;
                            }
                            return mb;
                          })
                        }
                        return cht;
                      })
                    )}
                  }
                );
              }
            });
          }
        });
      });
    }
  };

  const onChangeGroupData = () => {
    if (changeGroupData) {
      squadService?.groups.list((grps) =>
        grps.forEach((gp) => {
          if(gp.groupId === changeGroupData.groupId){
            squadService?.groups.editGroupInfo({
              groupId: changeGroupData.groupId,
              groupName: changeGroupData.groupName,
              members: changeGroupData.members,
              description: changeGroupData.description
            }, (success: boolean) => {
              if(success){
                setGroups(groups);
                setChangeGroupData(undefined);
              }
            });
          }
        })
      );
    }
  }

  const createGroup = () => {
    squadService?.groups.create(groupToCreate, (parsedGroup) => {
      if(parsedGroup){
        squadService?.groups.list((groups) => {
          setGroups(groups);
          setGroupToCreate(undefined);
        })
      }
    })
  }
 
  useEffect(onChatsChange, [chats]);
  useEffect(onNewFavorite, [newFavorite]);
  useEffect(checkSearchMessages, [activeChat.messageLoaded]);
  useEffect(onActiveChatChange, [activeChat]);
  useEffect(onUserDataChange, [changedUserData]);
  useEffect(onUserPasswordChange, [changedUserPassword]);
  useEffect(onChangeStatus, [user.status]);
  useEffect(forwardMessage, [forwardMessageList]);
  useEffect(onSearchContacts, [contactsSearch]);
  useEffect(onSearchContactsOutsideUserContacts, [showModalAddContact]);
  useEffect(onAddingContacts, [toggleAddingContacts]);
  useEffect(onRemovingGroupMember, [removedMember, groupToRemoveContact]);
  useEffect(onLeavingGroup, [memberQuit, groupIdOfLeaveRequest]);
  useEffect(onRemovingContact, [removedContact]);
  useEffect(onAddingMembers, [groupToInsertMember, checkedMembers]);
  useEffect(onToggleMemberRole, [toggleMemberRole]);
  useEffect(onChangeGroupData, [changeGroupData]);
  useEffect(onAddingContactOnGroupList, [clickedContact])
  useEffect(createGroup, [groupToCreate]);

  function onRemovingContact() {
    if (removedContact) {
      contacts.forEach((ctc) => {
        if (ctc.id === removedContact.id) {
          if (ctc.added)
            squadService?.contacts.remove(ctc, (success: boolean) => {
              if (success) {
                squadService?.contacts.list((ctcs) => {
                  setContacts(ctcs);
                  setRemovedContact(undefined);
                });
              }
            });
        }
      });
    }
  }

  function onUserPasswordChange() {
    if (changedUserPassword) {
      squadService?.user.changePassword(changedUserPassword, (password) => {
        if (password) {
          setChangedUserPassword({ oldPassword: "", newPassword: "" });
        }
      });
    }
  }

  function onUserDataChange() {
    if (changedUserData) {
      squadService?.user.changeUserData(changedUserData, (chgd) => {
        if (chgd) {
          setUser({
            ...user,
            email: changedUserData.email,
            name: changedUserData.name,
          });
        }
      });
    }
  }

  function onNewFavorite() {
    if (newFavorite) {
      const newFavoriteLoad = { ...newFavorite };
      squadService?.contacts.setFavorite(newFavorite, (favorite) => {
        if (favorite) {
          setChats(
            chats.map((cht) => {
              if (cht.jid === newFavoriteLoad.jid) {
                cht.favorite = !newFavoriteLoad.favorite;
              }
              return cht;
            })
          );
          setGroups(
            groups.map((grp) => {
              if (grp.jid === newFavoriteLoad.jid) {
                return {
                  ...grp,
                  favorite: !newFavoriteLoad.favorite,
                };
              }
              return grp;
            })
          );
          setContacts(
            contacts.map((ctt) => {
              if (ctt.jid === newFavoriteLoad.jid) {
                return {
                  ...ctt,
                  favorite: !newFavoriteLoad.favorite,
                };
              }
              return ctt;
            })
          );
        }
      });
      setNewFavorite(undefined);
    }
  }
  function chatCommunicatorSubscribe(event: string, data: any) {
    switch (event) {
      case Events.ONLINE:
        squadService?.chats.list((receivedChats) => {
          const chats: IChat[] = [];
          receivedChats.forEach((cht) => {
            if (!chats.find((chat) => chat.id === cht.id)) {
              chats.push(cht);
            }
          });
          setChats(chats);
          //populating groups
          squadService?.groups.list((groups) => {
            setGroups(groups);
            //Joining Groups Rooms in XMPP
            squadService?.chat?.joinGroups(groups.map((group) => group.jid));
          });
          //populating contacts
          squadService?.contacts.list((contacts) => setContacts(contacts));

          //populating chat's with messages

          // receivedChats.forEach((chat) => {
          //   squadService?.messages.list(chat, (messages) => {
          //     const chatWithMessages = { ...chat };
          //     chatWithMessages.messages = chatWithMessages.messages.concat(
          //       messages
          //     );
          //     setChats([
          //       ...receivedChats.filter((cht) => chat.id !== cht.id),
          //       chatWithMessages,
          //     ]);
          //   });
          // });
        });
        break;
      case Events.MESSAGE:
        if (data.fromUser === user.jid) break;
        let find = false;

        let newChats: IChat[] = [];
        chats.forEach((chat) => {
          if (
            data.fromUser.split("@")[0] === chat.jid.split("@")[0] ||
            data.toUser.split("@")[0] === chat.jid.split("@")[0]
          ) {
            find = true;
            if (!chat.messages.filter((msg) => msg.id === data.id)[0])
              chat.messages.push(data);
            if (chat.id === activeChat.id) {
              let chatTemporary = { ...chat };
              setActiveChat(chatTemporary);
            } else {
              chat.unRead++;
            }
            newChats.unshift(chat);
          } else {
            newChats.push(chat);
          }
        });

        if (!find) {
          let newChat: IChat = {} as IChat;

          squadService?.contacts.list((listContacts) => {
            listContacts.forEach((c) => {
              if (c.jid === data.fromUser) {
                newChat = {
                  id: c.id,
                  jid: c.jid,
                  name: c.name,
                  profilePicture: c.profilePicture,
                  status: c.status,
                  unRead: 1,
                  chatType: ChatType.USER,
                  inputMessage: "",
                  isTyping: false,
                  messages: [],
                  favorite: c.favorite,
                  added: c.added,
                };
                newChat.messages.push(data);
                newChats.unshift(newChat);
              }
            });
          });
        }
        setChats(newChats);

        break;
      case Events.RECEIVED:
        setChats(
          chats.map((chat) => {
            if (chat.messages.find((msg) => msg.id === data.id))
              chat.messages = chat.messages.map((message) => {
                message.deliverStatus = DeliverStatus.DELIVERED;
                return message;
              });
            return chat;
          })
        );
        break;
      case Events.DISPLAYED:
        setChats(
          chats.map((chat) => {
            if (chat.messages.find((msg) => msg.id === data.id))
              chat.messages = chat.messages.map((message) => {
                message.readStatus = ReadStatus.READ;
                return message;
              });
            return chat;
          })
        );
        break;
      case Events.COMPOSING:
        setChats(
          chats.map((chat) => {
            if (chat.jid === data) {
              chat.isTyping = true;
              if (activeChat.jid === data) setActiveChat(chat);
              deleteTypingTimeout(chat.jid);
              setTypingTimeout(chat.jid);
            }
            return chat;
          })
        );
        break;
      case Events.ACTIVE:
        setChats(
          chats.map((chat) => {
            if (chat.jid === data) {
              chat.isTyping = false;
              if (activeChat.jid === data) {
                let chatUpdate = { ...chat };
                setActiveChat(chatUpdate);
              }
              deleteTypingTimeout(chat.jid);
            }
            return chat;
          })
        );
        break;
      case Events.PRESENCE:
        setChats(
          chats.map((cht) => {
            if (
              cht.jid === data.from.split("/")[0] &&
              !cht.jid.includes("@conference.")
            ) {
              cht.status = data.status;
            }
            return cht;
          })
        );
        setContacts(
          contacts.map((ctc) => {
            if (
              ctc.jid === data.from.split("/")[0] &&
              !ctc.jid.includes("@conference.")
            ) {
              ctc.status = data.status;
            }
            return ctc;
          })
        );
        break;
    }
  }
  const deleteTypingTimeout = (jid: string) => {
    clearTimeout(typingTimeout.get(jid)?.timeout || setTimeout(() => {}, 1));
    typingTimeout.delete(jid);
  };
  const setTypingTimeout = (jid: string) => {
    const timeoutData: TypingTimeoutData = {
      msgStr: "",
      timeout: setTimeout(() => {
        setChats(
          chats.map((cht) => {
            if (cht.jid === jid) {
              cht.isTyping = false;
              if (activeChat.jid === jid) setActiveChat(cht);
            }
            return cht;
          })
        );
      }, 20000),
    };
    typingTimeout.set(jid, timeoutData);
  };

  squadService?.updateChatSubscribeFuncition(chatCommunicatorSubscribe);
  // Comentado para evitar warnings no console enquanto não é usado
