import React, { useState, useEffect } from "react";
import SimpleBar from "simplebar-react";
import { Spinner } from "reactstrap";
import { useChat } from "../contexts/ChatContext";
import ChatMessage from "./ChatMessage";
import "./../assets/css/reply-msg.scss";
import { useAuth } from "../contexts/AuthContext";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import Swal from "sweetalert2";
import ChatMessageDivisionData from "./ChatMessageDivisionData";
import EventNotification from "./EventNotification";

const scrollableNodeRef = React.createRef<HTMLDivElement>();

const ChatBody: React.FC = () => {
  const { activeChat, replyMsg, chats, setChats } = useChat();
  const [showButtonBottom, setShowButtonBottom] = useState(false);
  const { user } = useAuth();

  const onAddMessage = (file: File) => {
    setChats(
      chats.map((item) => {
        if (activeChat)
          if (item.id === activeChat?.id) {
            item.messages.push({
              id: `${item.messages.length + 1}`,
              userName: user.name,
              message: "Carregando...",
              time: "",
              isImageMessage: false,
              isFileMessage: false,
              isVideoMessage: false,
              isAudioMessage: false,
              fileUrl: "",
              sendFile: file,
              toUser: activeChat.id,
              fromUser: user.id,
              deliverStatus: DeliverStatus.SENDING,
              readStatus: ReadStatus.UNREAD,
              deliverTimestamp: "",
              readTimestamp: "",
              content: "metadata",
              contentType: "type",
              isReplyMessage: false,
              isForwardMessage: false,
            });
          }
        return item;
      })
    );
  };

  const onScroll = (ev: any) => {
    if (ev?.target?.scrollTop <= ev?.target?.scrollHeight - 1000) {
      setShowButtonBottom(true);
    } else {
      setShowButtonBottom(false);
    }
  };

  const scrolltoBottom = () => {
    scrollableNodeRef.current?.scrollTo({
      top: 99999999,
    });
    setShowButtonBottom(false);
  };

  const checkScroll = () => {
    scrolltoBottom();
    if (scrollableNodeRef?.current) {
      scrollableNodeRef.current.onscroll = onScroll;
    }
  };

  const checkLoading = () => {
    if (!activeChat.messageLoaded) {
      scrolltoBottom();
    }
  };

  useEffect(checkLoading, [activeChat.messageLoaded]);
  useEffect(checkScroll, [activeChat]);

  const dropHandler = (ev: React.DragEvent<HTMLElement>) => {
    console.log("File(s) dropped");

    // Impedir o comportamento padrão (impedir que o arquivo seja aberto)
    ev.preventDefault();

    if (ev.dataTransfer.items) {
      // Use a interface DataTransferItemList para acessar o (s) arquivo (s)
      for (var i = 0; i < ev.dataTransfer.items.length; i++) {
        // Se os itens soltos não forem arquivos, rejeite-os
        if (ev.dataTransfer.items[i].kind === "file") {
          var file = ev.dataTransfer.items[i].getAsFile();
          if (file && file.size > 15728640) {
            Swal.fire({
              icon: "error",
              title: "Oops...",
              text: "File size must be less than 15mb!",
              footer: "",
              customClass: {
                popup: "swal-mini-popup-file-size"
              }
            });
            return;
          } else {
            if (file) {
              onAddMessage(file);
            }
          }
        }
      }
    } else {
      // Use a interface DataTransfer para acessar o (s) arquivo (s)
      // eslint-disable-next-line no-redeclare
      for (var i = 0; i < ev.dataTransfer.files.length; i++) {
        console.log(
          "ELSE ... file[" + i + "].name = " + ev.dataTransfer.files[i].name
        );
      }
    }
  };

  return (
    <>
      {/* <DragNDropCaller /> */}
      <SimpleBar
        className={`chat-conversation ${replyMsg ? "reply" : ""} p-3 p-lg-4`}
        id="messages"
        scrollableNodeProps={{ ref: scrollableNodeRef }}
      >
        <div className={`d-flex justify-content-center`}>
          <Spinner
            color="primary"
            className={`${activeChat.messageLoaded ? "" : "d-none"}`}
          />
        </div>
        <ul
          onDragOver={(e) => e.preventDefault()}
          onDrop={dropHandler}
          className={`list-unstyled mb-0 ${
            activeChat.messageLoaded ? "d-none" : ""
          }`}
        >
          {activeChat.messages.map((message, key) => (
            <div key={key}>
              <ChatMessageDivisionData
                message={message}
                messages={activeChat.messages}
                i={key}
              />
              {message.eventId ? 
                <EventNotification msg={message}/>
              :
                <ChatMessage activeChat={activeChat} message={message} />
              }
            </div>
          ))}
        </ul>
      </SimpleBar>
      {showButtonBottom && (
        <div
          className="btn-chat-down"
          style={replyMsg ? { bottom: "190px" } : {}}
          onClick={scrolltoBottom}
        >
          <i className="ri-arrow-down-s-line"></i>
        </div>
      )}
    </>
  );
};

export default ChatBody;
