import React from "react";
import { Media, Badge } from "reactstrap";
import { Link } from "react-router-dom";
import { useChat } from "../contexts/ChatContext";
import { useGroup } from "../contexts/GroupContext";
import { useContact } from "../contexts/ContactContext";
import goldenstar from "./../assets/images/ic-star-fill.svg";
import star from "./../assets/images/ic-star.svg";
import { useTranslation } from "react-i18next";
import SimpleBar from "simplebar-react";
import IGroup from "../alias/IGroup";

interface IPros {
  recentGroupList: IGroup[];
}

const GroupList: React.FC<IPros> = ({ recentGroupList }) => {
  const { chats } = useChat();
  const { openGroup } = useGroup();
  const { setNewFavorite } = useContact();
  const { t } = useTranslation();
  const unRead = (groupId: string) => {
    let aux = 0;
    chats.forEach((item) => {
      if (item.groupId === groupId) {
        aux = item.unRead;
      }
    });
    return aux;
  };

  return (
    <SimpleBar
      style={{ maxHeight: "100%" }}
      className="p-4 chat-message-list chat-group-list"
    >
      <ul className="list-unstyled chat-list">
        {recentGroupList.map((group, key) => (
          group.favorite &&
          <li key={key}>
            <Link to="#" onClick={() => openGroup(group)}>
              <Media className="align-items-center">
                <div className="chat-user-img mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {group.name.trim().toUpperCase().charAt(0)}
                    </span>
                  </div>
                </div>
                <Media body className="overflow-hidden">
                  <h5 className="text-truncate font-size-14 mb-0">
                    {group.name.trim()}
                    
                    <div style={{display: "flex",
                    justifyContent: "flex-end",
                    alignItems: "flex-end",
                    position: "relative",
                    bottom: 13.5
                    }}>
                    {<img alt="star" onClick=
                        {() =>
                          {
                          setNewFavorite(group);
                          }
                        }
                        src={group.favorite ? goldenstar : star} style={{position: "relative", right: 2, top: 2}}
                    />}
                    </div>
                    
                    
                    {unRead(group.groupId) !== 0 ? (
                      <Badge
                        color="none"
                        pill
                        className="badge-soft-danger float-right"
                      >
                        {unRead(group.groupId) >= 99
                          ? 99 + "+"
                          : unRead(group.groupId)}
                      </Badge>
                    ) : null}

                    {false /* New Group n達o utilizado */ && (
                      <Badge
                        color="none"
                        pill
                        className="badge-soft-danger float-right"
                      >
                        {t("New")}
                      </Badge>
                    )}
                  </h5>
                </Media>
              </Media>
            </Link>
          </li>
        ))}
      </ul>
      <ul className="list-unstyled chat-list">
        {recentGroupList.map((group, key) => (
          !group.favorite &&
          <li key={key}>
            <Link to="#" onClick={() => openGroup(group)}>
              <Media className="align-items-center">
                <div className="chat-user-img mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {group.name.trim().toUpperCase().charAt(0)}
                    </span>
                  </div>
                </div>
                <Media body className="overflow-hidden">
                  <h5 className="text-truncate font-size-14 mb-0">
                    {group.name.trim()}

                    <div
                      style={{
                        display: "flex",
                        justifyContent: "flex-end",
                        alignItems: "flex-end",
                        position: "relative",
                        bottom: 13.5,
                      }}
                    >
                      {
                        <img
                          alt="star"
                          onClick={() => {
                            setNewFavorite(group);
                          }}
                          src={group.favorite ? goldenstar : star}
                          style={{ position: "relative", right: 2, top: 2 }}
                        />
                      }
                    </div>

                    {unRead(group.groupId) !== 0 ? (
                      <Badge
                        color="none"
                        pill
                        className="badge-soft-danger float-right"
                      >
                        {unRead(group.groupId) >= 99
                          ? 99 + "+"
                          : unRead(group.groupId)}
                      </Badge>
                    ) : null}

                    {false /* New Group n達o utilizado */ && (
                      <Badge
                        color="none"
                        pill
                        className="badge-soft-danger float-right"
                      >
                        {t("New")}
                      </Badge>
                    )}
                  </h5>
                </Media>
              </Media>
            </Link>
          </li>
        ))}
      </ul>
    </SimpleBar>
  );
};

export default GroupList;
