import React from "react";
import { Media, Badge } from "reactstrap";
import { Link } from "react-router-dom";
import { useChat } from "../contexts/ChatContext";

//simple bar
import SimpleBar from "simplebar-react";

//alias
import IGroup from "../alias/IGroup";

interface IPros {
  recentGroupList: IGroup[];
}

const GroupList: React.FC<IPros> = ({ recentGroupList }) => {
  const { openGroup, chats } = useChat();

  const unRead = (groupId: string) => {
    let aux = 0;
    chats.forEach((item) => {
      if (item.groupId === groupId) {
        aux = item.unRead;
      }
    });
    return aux;
  };

  return (
    <SimpleBar
      style={{ maxHeight: "100%" }}
      className="p-4 chat-message-list chat-group-list"
    >
      <ul className="list-unstyled chat-list">
        {recentGroupList.map((group, key) => (
          <li key={key}>
            <Link to="#" onClick={() => openGroup(group)}>
              <Media className="align-items-center">
                <div className="chat-user-img mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {group.name.trim().toUpperCase().charAt(0)}
                    </span>
                    <div>TESTEESTRELINHAAQUI</div>
                  </div>
                </div>
                <Media body className="overflow-hidden">
                  <h5 className="text-truncate font-size-14 mb-0">
                    {group.name.trim()}
                    {unRead(group.groupId) !== 0 ? (
                      <Badge
                        color="none"
                        pill
                        className="badge-soft-danger float-right"
                      >
                        {unRead(group.groupId) >= 99
                          ? 99 + "+"
                          : unRead(group.groupId)}
                      </Badge>
                    ) : null}

                    {false /* New Group não utilizado */ && (
                      <Badge
                        color="none"
                        pill
                        className="badge-soft-danger float-right"
                      >
                        New
                      </Badge>
                    )}
                  </h5>
                </Media>
              </Media>
            </Link>
          </li>
        ))}
      </ul>
    </SimpleBar>
  );
};

export default GroupList;
