import React, { useEffect, useState } from "react";
import { Button, Card, Media, Badge } from "reactstrap";
import SimpleBar from "simplebar-react";
import CustomCollapse from "./CustomCollapse";
import { useTranslation } from "react-i18next";
import { useChat } from "../contexts/ChatContext";
import AttachedFilesTab from "./AttachedFilesTab";
import ReactImageFallback from "react-image-fallback";
import defaultAvatar from "../assets/images/default.png";
import { onImgError } from "../helpers/ImageUtils";
import { useGroup } from "../contexts/GroupContext";
import { useContact } from "../contexts/ContactContext";
import { useAuth } from "../contexts/AuthContext";
import { useModal } from "./../contexts/ModalContext";
import ModalAddMember from "./ModalAddMember";
import { Input } from "@material-ui/core";
import UserStatusOnProfileSidebar from "./UserStatusOnProfileSidebar";
import ChangeGroupNameButton from "./ChangeGroupNameButton";
import LeaveGroupButton from './LeaveGroupButton';
import InfoHeadOnSidebar from './InfoHeadOnSidebar';
import GroupMember from './GroupMember';
interface IPros {
  show: boolean;
  setShow(value: boolean): void;
}

const ChatProfileSidebar: React.FC<IPros> = ({ show, setShow }) => {
  const [isOpen1, setIsOpen1] = useState(true);
  const [isOpen2, setIsOpen2] = useState(false);
  const [newGroupName, setGroupName] = useState("");
  const { user } = useAuth();
  const {
    activeChat,
    chats,
    setChats,
    setActiveChat,
  } = useChat();
  const {
    groups,
    setGroups,
  } = useGroup();
  const {
    contacts,
    setContacts,
  } = useContact();
  const { setShowModalAddMember } = useModal();
  const toggleCollapse1 = () => {
      setIsOpen1(!isOpen1);
      setIsOpen2(false);
  };
  const toggleCollapse2 = () => {
    setIsOpen1(false);
    setIsOpen2(!isOpen2);
  };
  const closeUserSidebar = () => {
    setShow(false);
  };
  
  const getAttachedFiles = () => {
    if (show && activeChat.attachedFileList === undefined) {
      setActiveChat({ ...activeChat, attachedFileList: [] });
    } else if (!show) {
      setActiveChat({ ...activeChat, attachedFileList: undefined });
    }
  };
  useEffect(getAttachedFiles, [show]);
  const closeUserSidebarOnActiveChatChange = () => {
    if (show && activeChat.attachedFileList === undefined) closeUserSidebar();
  };
  useEffect(closeUserSidebarOnActiveChatChange, [activeChat]);
  const actualMember = activeChat.members?.find(
    (member) => member.userId === user.id
  );
  const isAdmin = actualMember?.role === "admin";
  //const groupAdmins = activeChat.members?.filter((member) => member.role === "admin");
  const actualMembers = activeChat.members?.map((mb) => {
    return mb.userId;
  });
  const isGroup = activeChat.groupId;
  const membersArrayPreventingDuplicateDisplayMember = activeChat.members?.filter((item,index,arrayCompleto) => arrayCompleto.findIndex(item2 => (item.userId === item2.userId)) === index);

  return (
    <React.Fragment>
      <div
        style={{ display: show ? "block" : "none" }}
        className="user-profile-sidebar "
      >
        <div className="d-flex" style={{ justifyContent: "center" }}>
          {isGroup && isAdmin && (
            <div className="px-3 px-lg-4 pt-3 pt-lg-4">
              <div className="user-chat-nav text-right">
                <Button
                  color="none"
                  type="button"
                  className="nav-btn"
                  onClick={() => setShowModalAddMember(true)}
                >
                  <i className="ri-user-add-line"></i>
                </Button>
              </div>
            </div>
          )}

          <ModalAddMember />

          {isGroup && (
            <LeaveGroupButton activeChat={activeChat} actualMember={actualMember}/>
          )}

          {isGroup && (
            <div className="px-3 px-lg-4 pt-3 pt-lg-4">
              <div className="user-chat-nav text-right">
                <Button
                  color="none"
                  type="button"
                  onClick={closeUserSidebar}
                  className="nav-btn"
                  id="user-profile-hide"
                >
                  <i className="ri-close-line"></i>
                </Button>
              </div>
            </div>
          )}
        </div>
        
        <div className="text-center p-4 border-bottom">
          <div className="mb-4 d-flex justify-content-center">
            {!activeChat.profilePicture ? (
              <div className="avatar-lg">
                <span className="avatar-title rounded-circle bg-soft-primary text-primary font-size-24">
                  {activeChat.name.trim().charAt(0)}
                </span>
              </div>
            ) : (
              <ReactImageFallback
                src={activeChat.profilePicture}
                onError={() => {
                  if (activeChat.jid.includes("@conference."))
                    onImgError(
                      chats,
                      groups,
                      contacts,
                      setChats,
                      setGroups,
                      setContacts,
                      activeChat
                    );
                }}
                fallbackImage={defaultAvatar}
                className="rounded-circle avatar-lg img-thumbnail"
                alt="chatvia"
              />
            )}
          </div>
          <div className="d-flex">
            {isGroup ? (
              <Input
                inputProps={{ min: 0, style: { textAlign: "center" } }}
                placeholder={activeChat.name}
                disableUnderline
                fullWidth
                onChange={(e) => setGroupName(e.target.value)}
              />
            ) : (
              <div>{activeChat.name}</div>
            )}
          </div>
          
          {isGroup && newGroupName.length > 0 ? (
            <ChangeGroupNameButton newGroupName={newGroupName} setGroupName={setGroupName} actualMembers={actualMembers}/>
          ) : (
            isGroup && <i className="ri-edit-line"></i>
          )}

          {activeChat.status && (
            <UserStatusOnProfileSidebar/>
          )}

        </div>

        <SimpleBar
          style={{ maxHeight: "100%" }}
          className="p-4 user-profile-desc"
        >
          <div id="profile-user-accordion" className="custom-accordion">
            <InfoHeadOnSidebar activeChat={activeChat} toggleCollapse1={toggleCollapse1} isOpen1={isOpen1} />

            {activeChat.jid.includes("@conference.") && (
              <Card className="mb-1 shadow-none border">
                <CustomCollapse
                  title={`Members (${membersArrayPreventingDuplicateDisplayMember?.length})`}
                  iconClass="ri-group-line"
                  isOpen={isOpen2}
                  toggleCollapse={toggleCollapse2}
                >
                  {membersArrayPreventingDuplicateDisplayMember?.map((member) => (
                    <GroupMember defaultAvatar={defaultAvatar} isAdmin={isAdmin} member={member}/>
                  ))}
                </CustomCollapse>
              </Card>
            )}
            <AttachedFilesTab />
          </div>
        </SimpleBar>
      </div>
    </React.Fragment>
  );
};

export default ChatProfileSidebar;
