import React, { useEffect, useState } from "react";
import { Input, Label } from "reactstrap";
import IParticipant from "../alias/IParticipant";
import { useChat } from "../contexts/ChatContext";
import { useConference } from "../contexts/";
import { SearchParticipant } from "../helpers/SearchParticipant";
import sortParticipantGroup from "../helpers/SortParticipantGroup";
import { IConference } from "../alias/IConference";

interface IProps {
  search?: string;
  handleCheckParticipant(selected: boolean, participant: IParticipant): void;
  checkedParticipants: IParticipant[];
}

const SelectMember: React.FC<IProps> = ({ search, handleCheckParticipant, checkedParticipants }) => {
  const {
    participants,
    conferenceList,
  } = useConference();
  const { activeChat } = useChat();
  
  let actualConference = conferenceList.find((cfr: IConference) => (
    cfr.id === activeChat.id
  ));  

  let actualParticipantMembers = actualConference?.members?.map((mbrs) => {
    return mbrs.userId;
  });

  let [participantStorage, setParticipantStorage] = useState<IParticipant[]>([]);
  const init = () => {
    setParticipantStorage(
      participants.filter((ptc: IParticipant) => {
        if(actualParticipantMembers?.find((id) => (id === ptc.userUuid))){
          return false;
        }
        return true;
      }
    ));
  }
  
  useEffect(init, []);

  
  return (
    <div className="mt-2">
      {sortParticipantGroup(
        SearchParticipant(participantStorage, search ? search : "")
      ).map((participant, key) => (
        <div key={key}>
          <div className="ml-3 pl-1 font-weight-bold text-primary">
            {participant.group}
          </div>

          <ul className="list-unstyled participant-list mb-2">
            {participant.children.map((child) => (
              <li key={child.id}>
                <div className="custom-control custom-checkbox">
                  <Input
                    type="checkbox"
                    className="custom-control-input"
                    onChange={(e) => {
                      handleCheckParticipant(e.target.checked, child);
                    }}
                    id={"participantCheck" + child.id}
                    checked={ checkedParticipants.find((item) => item.id === child.id) ? true : false }
                  />
                  <Label
                    className="custom-control-label"
                    htmlFor={"participantCheck" + child.id}
                  >
                    {child.name}
                  </Label>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default SelectMember;
