import React, { useState, useEffect } from "react";
import {
  Button,
  Input,
  Row,
  Col,
  UncontrolledTooltip,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  Label,
  Form,
} from "reactstrap";
import { Picker } from "emoji-mart";
import "emoji-mart/css/emoji-mart.css";

import { Emoji } from "emoji-mart";

const ChatInput: React.FC = () => {
  const [textMessage, settextMessage] = useState("");
  const [isOpen, setisOpen] = useState(false);
  const [file, setfile] = useState({
    name: "",
    size: "",
  });
  const [fileImage, setfileImage] = useState("");

  console.log(textMessage);

  const toggle = () => setisOpen(!isOpen);

  //function for text input value change
  const handleChange = (e: any) => {
    settextMessage(e.target.value);
  };

  //function for add emojis
  const addEmoji = (e: any) => {
    let emoji = e.native;
    console.log(emoji);
    settextMessage(textMessage + emoji);
  };

  //function for file input change
  const handleFileChange = (e: any) => {
    if (e.target.files.length !== 0)
      setfile({
        name: e.target.files[0].name,
        size: e.target.files[0].size,
      });
  };

  //function for image input change
  const handleImageChange = (e: any) => {
    if (e.target.files.length !== 0)
      setfileImage(URL.createObjectURL(e.target.files[0]));
  };

  //function for send data to onaddMessage function(in userChat/index.js component)
  const onaddMessage = (e: any, textMessage: any) => {
    e.preventDefault();
    //if text value is not emptry then call onaddMessage function
    if (textMessage !== "") {
      /* props.onaddMessage(textMessage, "textMessage"); */
      settextMessage("");
    }

    //if file input value is not empty then call onaddMessage function
    if (file.name !== "") {
      /* props.onaddMessage(file, "fileMessage"); */
      setfile({
        name: "",
        size: "",
      });
    }

    //if image input value is not empty then call onaddMessage function
    if (fileImage !== "") {
      /* props.onaddMessage(fileImage, "imageMessage"); */
      setfileImage("");
    }
  };

  return (
    <React.Fragment>
      <div className="p-3 p-lg-4 border-top mb-0">
        <Form onSubmit={(e) => onaddMessage(e, textMessage)}>
          <Row noGutters>
            <Col>
              <div>
                <Input
                  type="text"
                  value={textMessage}
                  onChange={handleChange}
                  className="form-control form-control-lg bg-light border-light"
                  placeholder={"Send message..."}
                ></Input>
                <div
                  style={{
                    color: "red",
                    fontSize: 12,
                  }}
                >
                  {textMessage.length > 9900
                    ? 10000 - textMessage.length + " caracteres restantes"
                    : null}
                </div>
              </div>
            </Col>
            <Col xs="auto">
              <div className="chat-input-links ml-md-2">
                <ul className="list-inline mb-0">
                  <li className="list-inline-item">
                    <ButtonDropdown
                      className="emoji-dropdown"
                      direction="up"
                      isOpen={isOpen}
                      toggle={toggle}
                    >
                      <DropdownToggle
                        id="emoji"
                        color="link"
                        className="text-decoration-none font-size-16 btn-lg waves-effect"
                      >
                        <i className="ri-emotion-happy-line"></i>
                      </DropdownToggle>
                      <DropdownMenu className="dropdown-menu-lg-right">
                        <Picker set="apple" onSelect={addEmoji} />
                        <Emoji emoji="santa" set="apple" size={16} />
                      </DropdownMenu>
                    </ButtonDropdown>
                    <UncontrolledTooltip target="emoji" placement="top">
                      Emoji
                    </UncontrolledTooltip>
                  </li>
                  <li className="list-inline-item input-file">
                    <Label
                      id="files"
                      className="btn btn-link text-decoration-none font-size-16 btn-lg waves-effect"
                    >
                      <i className="ri-attachment-line"></i>
                      <Input
                        onChange={(e) => handleFileChange(e)}
                        type="file"
                        name="fileInput"
                      />
                    </Label>
                    <UncontrolledTooltip target="files" placement="top">
                      Attached File
                    </UncontrolledTooltip>
                  </li>
                  <li className="list-inline-item input-file">
                    <Label
                      id="images"
                      className="mr-1 btn btn-link text-decoration-none font-size-16 btn-lg waves-effect"
                    >
                      <i className="ri-image-fill"></i>
                      <Input
                        onChange={(e) => handleImageChange(e)}
                        accept="image/*"
                        type="file"
                        name="fileInput"
                      />
                    </Label>
                    <UncontrolledTooltip target="images" placement="top">
                      Images
                    </UncontrolledTooltip>
                  </li>
                  <li className="list-inline-item">
                    <Button
                      type={textMessage.length < 10000 ? "submit" : "button"}
                      color="primary"
                      className="font-size-16 btn-lg chat-send waves-effect waves-light"
                    >
                      <i className="ri-send-plane-2-fill"></i>
                    </Button>
                  </li>
                </ul>
              </div>
            </Col>
          </Row>
        </Form>
      </div>
    </React.Fragment>
  );
};

export default ChatInput;
