import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { Card, Media, Badge, Button } from 'reactstrap';
import ReactImageFallback from "react-image-fallback";
import { Link } from "react-router-dom";
import { useContact } from '../contexts/ContactContext';
import { useAuth } from '../contexts/AuthContext';
import { useChat } from '../contexts/ChatContext';
import { useGroup } from '../contexts/GroupContext';
import IChat from '../alias/IChat';
import IMember from '../alias/IMember';
import { useConference } from '../contexts/ConferenceContext';
import ScreenType from "../enuns/ScreenType";
import { useMainBox } from "../contexts/MainBoxContext";
import ModalGenerics from './ModalGenerics';
import { toastSuccess } from './../helpers/toastCaller';

interface IProps {
  member: IMember;
  defaultAvatar: any;
  isAdmin: any;
}

const ChangeGroupNameButton : React.FC<IProps> = ({ isAdmin, defaultAvatar, member, }) => {
    const { activeChat, chats, openChat } = useChat();
    const [showRemoveMember, setShowRemoveMember] = useState<boolean>(false);
    const [showModalToggleRole, setShowModalToggleRole] = useState<boolean>(false);
    const [showModalToggleRoleAdminOrModerator, setShowModalToggleRoleAdminOrModerator] = useState<boolean>(false);
    const { setClickedContact, contacts, contactsOutsideUserContacts, openContact } = useContact();
    const { setScreen } = useMainBox();
    const { user } = useAuth();
    const { t } = useTranslation();

    const openChatAction = (chat: IChat) => {
        openChat(chat);
        setScreen(ScreenType.CHAT);
    };

    const toggleMemberRole = (member: IMember, activeChat: IChat) => {
      setToggleMemberRole({
        mbId: member.userId,
        gpId: activeChat.groupId
          ? activeChat.groupId
          : "",
        mbRole: member.role,
        mb: member,
      });
    }


      /* if(activeChat.members){
        let userThatWeWantId = activeChat?.members.map((mbr) => {
          if(mbr.userId === )
        })
      } */

      

    return (<>
    <ModalGenerics
      setShow={setShowRemoveMember}
      show={showRemoveMember}
      title={t("Remove") + " " + member.name + "?"}
      children={
        <div>
          <>
          <Button type="button" color="primary" 
          >{t("Confirm")}</Button>
          <Button type="button" color="link" onClick={() => 
            {
              setShowRemoveMember(!showRemoveMember);
            }
          }>
            {t("Cancel")}
          </Button>
          </>
        </div>
      }
      />
    <ModalGenerics
      setShow={setShowModalToggleRole}
      show={showModalToggleRole}
      title={t("Turn") +
      " " +
      member.name +
      " " +
      t("a member?")}
      children={
        <>
          <Button type="button" color="link" onClick={() => setShowModalToggleRole(!showModalToggleRole)}>
              {t("Cancel")}
          </Button>
          {
            activeChat.conferenceRoomParticipants ?
              <Button type="button" color="primary" 
              onClick={() => {
                toastSuccess(t("Sucess!")); 
                setShowModalToggleRole(!showModalToggleRole);
              }}>
                {t("Confirm")}
              </Button>
                :
              <Button type="button" color="primary" 
              onClick={() => 
                {
                  toastSuccess(t("Sucess!")); 
                  toggleMemberRole(member, activeChat);
                  setShowModalToggleRole(!showModalToggleRole);
                }
              }>
                {t("Confirm")}
              </Button>
          }
        </>
      }
      />


      <ModalGenerics
      setShow={setShowModalToggleRoleAdminOrModerator}
      show={showModalToggleRoleAdminOrModerator}
      title={t("Turn") + " " + member.name + " " + t("an administrator?")}
      children={
        <>
          <Button type="button" color="link" onClick={() => setShowModalToggleRoleAdminOrModerator(!showModalToggleRoleAdminOrModerator)}>
              {t("Cancel")}
          </Button>
          {
            activeChat.conferenceRoomParticipants ?
              <Button type="button" color="primary" 
              onClick={() => {
                toastSuccess(t("Sucess!")); 
                setShowModalToggleRoleAdminOrModerator(!showModalToggleRoleAdminOrModerator)
              }}>
                {t("Confirm")}
              </Button>
                :
              <Button type="button" color="primary" 
              onClick={() => 
                {
                  toastSuccess(t("Sucess!")); 
                  toggleMemberRole(member, activeChat)
                  setShowModalToggleRoleAdminOrModerator(!showModalToggleRoleAdminOrModerator);
                }
              }>
                {t("Confirm")}
              </Button>
          }
        </>
      }
      /> 

    <Card key={member.userId} className="p-2 mb-2">
    <Media className="align-items-center">
      <ReactImageFallback
        src={member.profilePicture}
        className="rounded-circle avatar-xs mr-2"
        alt="chatvia"
        fallbackImage={defaultAvatar}
      />
      <Media body>
        <div className="text-left">
          <h5 className="font-size-14 mb-1">
            <Link
              to="#"
              onClick={() => {
                chats.forEach((cht: any) => {
                  if (cht.id === member.userId) {
                    openChatAction(cht);
                    contacts.forEach((ctc: any) => {
                      if (ctc.id === member.userId) {
                        setClickedContact(ctc);
                      }
                    });
                  } else {
                    contactsOutsideUserContacts.forEach(
                      (ctcOut) => {
                        if (ctcOut.id === member.userId) {
                          openContact(ctcOut);
                          setClickedContact(ctcOut);
                        }
                      });
                    }
                  });
                }}
              >
                {member.name}
              </Link>

            {(((isAdmin || amIaModerator) && user.id !== member.userId) && (
              <span
                className="float-right ml-2"
                style={{ cursor: "pointer" }}
                onClick={() =>
                  setShowRemoveMember(true)
                }
              >
                <i className="ri-close-fill"></i>
              </span>
            ))}

              (
              <Badge
                color={activeChat.conferenceRoomParticipants ? "warning" : "danger"}
                className={activeChat.conferenceRoomParticipants ? "badge-soft-warning float-right" : "badge-soft-danger float-right"}
                onClick={() =>
                  (isAdmin || amIaModerator) && (member.userId !== user.id) &&
                  setShowModalToggleRole(true)
                }
                style={{ cursor: "pointer" }}
              >
                {activeChat.conferenceRoomParticipants ? t("Moderator") : t("Admin")}
              </Badge>
              ) : (
              <Badge
                color="success"
                className="badge-soft-success float-right"
                style={{ cursor: "pointer" }}
                onClick={() =>
                  (isAdmin || amIaModerator) && (member.userId !== user.id) &&
                  setShowModalToggleRoleAdminOrModerator(true)
                }
              >
                {t("Member")}
              </Badge>

          </h5>
        </div>
      </Media>
    </Media>
  </Card>
  </>
  );
}

export default ChangeGroupNameButton;
