import React, { createContext, useContext, useState } from "react";
import IConferenceMember from "../alias/IConferenceMember";

interface IParticipantsDrawerContext {
  participants: IConferenceMember[];
  setParticipants(value: IConferenceMember[]): void;
  show: boolean;
  setShow(value: boolean): void;
  description: string;
  setDescription(value: string): void;
}

export const ParticipantsDrawerContext = createContext<IParticipantsDrawerContext>(
  {} as IParticipantsDrawerContext
);

export const ParticipantsDrawerProvider: React.FC = ({ children }) => {
  const [participants, setParticipants] = useState<IConferenceMember[]>([]);
  const [show, setShow] = useState(false);
  const [description, setDescription] = useState("");

  return (
    <ParticipantsDrawerContext.Provider
      value={{
        participants,
        setParticipants,
        show,
        setShow,
        description,
        setDescription,
      }}
    >
      {children}
    </ParticipantsDrawerContext.Provider>
  );
};

export function useParticipantsDrawer() {
  return useContext(ParticipantsDrawerContext);
}
