import React, { useState, useEffect } from "react";
import {
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  UncontrolledDropdown,
} from "reactstrap";
import moment from "moment";

import IChat from "../alias/IChat";
import IMessage from "../alias/IMessage";

import ImageList from "./ImageList";
import FileList from "./FileList";

import { useTranslation } from "react-i18next";
import { useAuth } from "../contexts/AuthContext";

import DeliverStatus from "./../enuns/DeliverStatus";
import ReadStatus from "./../enuns/ReadStatus";

interface IProps {
  activeChat: IChat;
  message: IMessage;
}

const MessageGroup: React.FC<IProps> = ({ activeChat, message }) => {
  const [show, setShow] = useState(false);

  const toggle = () => setShow(!show);

  const { t } = useTranslation();
  const { user } = useAuth();

  //ao sair do chat atual, esconda o texto de +165 caracteres aberto
  useEffect(() => {
    setShow(false);
  }, [activeChat]);

  const deleteMessage = (id: string) => {};

  var labelToReadALongerText = ".. Ler mais";
  var smallerMessage = "";
  var biggerMessage = "";
  const MAXNUMBEROFCHARACTERSONAMESSAGE = 10000;
  const MAXNUMBERTODISPLAYMESSAGE = 165;

  console.log("usuario", user);
  console.log("msg", message);
  console.log("chat", activeChat);

  const messageText = () => {
    if (message.message) {
      if (message.message.length > MAXNUMBERTODISPLAYMESSAGE) {
        return (
          <p
            className="mb-0"
            style={{ wordBreak: "break-word", textAlign: "justify" }}
          >
            {!show
              ? (smallerMessage = message.message.slice(
                  0,
                  MAXNUMBERTODISPLAYMESSAGE
                )) && smallerMessage
              : null}
            {show
              ? (biggerMessage = message.message.slice(
                  MAXNUMBERTODISPLAYMESSAGE,
                  MAXNUMBEROFCHARACTERSONAMESSAGE
                )) &&
                message.message.slice(0, MAXNUMBERTODISPLAYMESSAGE) +
                  biggerMessage
              : null}

            <button
              className="hover"
              style={{
                border: 0,
                background: 0,
                color: "rgb(217, 237, 250)",
              }}
              onClick={toggle}
            >
              {!show ? labelToReadALongerText : null}
            </button>
          </p>
        );
      }
      if (message.message.length <= 164) {
        return (
          <p
            className="mb-0"
            style={{ wordBreak: "break-word", textAlign: "justify" }}
          >
            {message.message}
          </p>
        );
      }
    }
  };

  //clock: <i className="ri-time-line align-middle"></i>

  const messageFooter = () => {
    return (
      <p className="chat-time mb-0">
        <i
          style={{
            position: "relative",
            right: 8,
            top: 1,
            color: "rgb(114, 105, 239)",
          }}
          className={
            message.deliverStatus === DeliverStatus.QUEUED
              ? "ri-check-line align-middle"
              : ""
          }
        ></i>
        <i
          style={{
            position: "relative",
            right: 8,
            top: 1,
            color: "rgb(114, 105, 239)",
          }}
          className={
            message.deliverStatus === DeliverStatus.DELIVERED &&
            message.readStatus === ReadStatus.UNREAD
              ? "ri-check-double-line align-middle"
              : ""
          }
        ></i>
        <i
          style={{
            position: "relative",
            right: 8,
            top: 1,
            color: "green",
          }}
          className={
            message.readStatus === ReadStatus.READ
              ? "ri-check-double-line align-middle"
              : ""
          }
        ></i>
        <span
          className="align-middle"
          style={{ position: "relative", right: 4 }}
        >
          {moment(message.time).format("HH:mm")}
        </span>
      </p>
    );
  };

  const option = () => {
    if (!activeChat.isTyping) {
      return (
        <UncontrolledDropdown className="align-self-start">
          <DropdownToggle tag="a">
            <i className="ri-more-2-fill"></i>
          </DropdownToggle>
          <DropdownMenu>
            <DropdownItem>
              {t("Copy")}
              <i className="ri-file-copy-line float-right text-muted"></i>
            </DropdownItem>
            <DropdownItem>
              {t("Save")}{" "}
              <i className="ri-save-line float-right text-muted"></i>
            </DropdownItem>
            <DropdownItem onClick={toggle}>
              Forward{" "}
              <i className="ri-chat-forward-line float-right text-muted"></i>
            </DropdownItem>
            <DropdownItem onClick={() => deleteMessage(message.id)}>
              Delete{" "}
              <i className="ri-delete-bin-line float-right text-muted"></i>
            </DropdownItem>
          </DropdownMenu>
        </UncontrolledDropdown>
      );
    }
  };

  const photo = () => {
    if (activeChat.profilePicture) {
      return <img src={activeChat.profilePicture} alt="chatvia" />;
    } else {
      return (
        <div className="chat-user-img align-self-center mr-3">
          <div className="avatar-xs">
            <span className="avatar-title rounded-circle bg-soft-primary text-primary">
              {message.userName && message.userName.trim().charAt(0)}
            </span>
          </div>
        </div>
      );
    }
  };

  const isTyping = () => {
    if (activeChat.isTyping)
      return (
        <p className="mb-0">
          typing
          <span className="animate-typing">
            <span className="dot ml-1"></span>
            <span className="dot ml-1"></span>
            <span className="dot ml-1"></span>
          </span>
        </p>
      );
  };

  const image = () => {
    if (message.isImageMessage && false)
      return <ImageList image={message.fileUrl} />;
  };

  const file = () => {
    if (message.isFileMessage && false)
      return <FileList fileName={message.fileUrl} fileSize={message.fileUrl} />;
  };

  return (
    <li className={user.id === message.fromUser ? "right" : ""}>
      <div className="conversation-list">
        <div className="chat-avatar">{photo()}</div>

        <div
          className={
            "user-chat-content " +
            (user.id === message.fromUser
              ? "message-chat-left"
              : "message-chat-right")
          }
        >
          <div className="ctext-wrap">
            <div className="ctext-wrap-content">
              {messageText()}
              {image()}
              {file()}
              {messageFooter()}
              {isTyping()}
            </div>
            {option()}
          </div>
          <div className="conversation-name">{message.userName}</div>
        </div>
      </div>
    </li>
  );
};

export default MessageGroup;
