import React, { useState } from "react";
import { Input, UncontrolledTooltip, Label, Button } from "reactstrap";
import { useAuth } from "../contexts/AuthContext";
import { useChat } from "../contexts/ChatContext";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import { useTranslation } from "react-i18next";
import Lightbox from "react-image-lightbox";

const ChatInputAttachedFileButton: React.FC = () => {
  const { chats, setChats, activeChat } = useChat();
  const { user } = useAuth();
  const [ send, setSend ] = useState(false);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {

    let files = e.target.files;
    if (files?.length === 0) return;
    let file = files?.item(0);
    if (file && file.size > 15728640) {
      alert(
        "Tamanho de arquivo " +
          file.name +
          " não suportado. Por favor, envie um arquivo menor do que 15mb"
      );
      return;
    }
    
    if (file) {
      onAddMessage(file);
    }
  };

  const editButtons = [
    (
      <Button
        type="button"
        className="ril__toolbarItemChild ril__builtinButton"
        style={{
          paddingTop: 6,
          backgroundColor: 'red',
        }}
        onClick={() => {
          setSend(true);
        }}
      />
    ),
  ];

  const previewFileSending = (e: React.ChangeEvent<HTMLInputElement>) => {
    let files = e.target.files;
    if(files?.length === 0) return;
    let file = files?.item(0);
    if(file){
      toggleLightbox(file);
    }
    
    if(send === true){
      handleFileChange(e);
    }
  }

  const { t } = useTranslation();

  const onAddMessage = (file: File) => {
    setChats(
      chats.map((item) => {
        if (activeChat)
          if (item.id === activeChat?.id) {
            item.messages.push({
              id: `${item.messages.length + 1}`,
              userName: user.name,
              message: "Carregando...",
              time: "",
              isImageMessage: false,
              isFileMessage: false,
              isVideoMessage: false,
              isAudioMessage: false,
              fileUrl: "",
              sendFile: file,
              toUser: activeChat.id,
              fromUser: user.id,
              deliverStatus: DeliverStatus.SENDING,
              readStatus: ReadStatus.UNREAD,
              deliverTimestamp: "",
              readTimestamp: "",
              content: "metadata",
              contentType: "type",
              isReplyMessage: false,
              isForwardMessage: false,
            });
          }
        return item;
      })
    );
  };

  const [isOpen, setIsOpen] = useState(false);
  const [currentImage, setCurrentImage] = useState("null");
  const toggleLightbox: any = (currentImage: any) => {
    setIsOpen(!isOpen);
    setCurrentImage(currentImage);
  };

  //chamar a mesma lightbox no ctrl+v também

  return (
    <li className="list-inline-item input-file">
      <Label
        id="files"
        className="btn btn-link text-decoration-none font-size-16 btn-lg waves-effect"
      >
        <i className="ri-attachment-line"></i>
        <Input onChange={previewFileSending} type="file" name="fileInput" />
      </Label>
      <UncontrolledTooltip target="files" placement="top">
        {t("Attach File")}
      </UncontrolledTooltip>
      {isOpen && (
        <>
        <Lightbox
          mainSrc={currentImage}
          onCloseRequest={toggleLightbox}
          imageTitle="Image File"
          enableZoom={true}
          toolbarButtons={[
            editButtons
          ]}
        />
        </>
      )}
    </li>
  );
};

export default ChatInputAttachedFileButton;
