import React from 'react';
import IMessage from '../alias/IMessage';
import { useGroup } from '../contexts/GroupContext';

interface IGroupNotification{
    msg: IMessage;
}

const GroupNotification : React.FC<IGroupNotification> = ({ msg }) => {
    let userThatIsRemoving: any;
    const { groups, removedMember } = useGroup();
    switch(msg.eventId){
        case 6:
        groups.forEach((grp) => {
            if(grp.groupId === msg.fromUser.split('@')[0]){ //msg.fromUser é o id do grupo que ocorrerá o evento?
                grp.members.forEach((mb) => {
                    if(mb.userId === msg.eventBody?.split('@')[0]){
                        userThatIsRemoving = msg.eventBody;
                    };
                })
            }
        })
        return(
            <div className="card">
                <div className="card-body" style={{color: "red"}}>
                    {removedMember?.name} has been removed by {userThatIsRemoving}!
                </div>
            </div>
        );
        default:
            return <></>    
    }
}

export default GroupNotification;
