import React, { useState } from "react";
import {
  Button,
  Input,
  Row,
  Col,
  UncontrolledTooltip,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  Label,
  Form,
} from "reactstrap";
import { Picker, Emoji } from "emoji-mart";
import "emoji-mart/css/emoji-mart.css";
import { ReactMic } from "react-mic";

import { activeChat } from "../data/chat";

import { useSquadCommunicator } from "./../services/SquadCommunicator";

const ChatInput: React.FC = () => {
  const { sendFiles, pasteFiles } = useSquadCommunicator();
  const [textMessage, settextMessage] = useState("");
  const [isOpen, setisOpen] = useState(false);
  const [file, setFile] = useState({
    name: "",
    size: "",
  });
  const [fileImage, setFileImage] = useState("");
  const [record, setRecord] = useState(false);
  const [deleteAudio, setDeleteAudio] = useState(false);

  const toggle = () => setisOpen(!isOpen);

  const handleChange = (e: any) => {
    settextMessage(e.target.value);
  };

  const addEmoji = (e: any) => {
    let emoji = e.native;
    settextMessage(textMessage + emoji);
  };

  const handleFileChange = (e: any) => {
    if (e.target.files.length !== 0) {
      setFile({
        name: e.target.files[0].name,
        size: e.target.files[0].size,
      });
    }
    for (var i = 0; i < e.target.files.length; i++) {
      if (e.target.files[i].size > 15000) {
        alert(
          "Tamanho de arquivo " +
            e.target.files[i].name +
            " não suportado. Por favor, envie um arquivo menor do que 15mb"
        );
      } else {
        sendFiles(activeChat, e.target.files[i]);
      }
    }
  };

  const handlePaste = (e: any) => {
    if (e.clipboardData.files.length !== 0) {
      setFile({
        name: e.clipboardData.files[0].name,
        size: e.clipboardData.files[0].size,
      });
    }
    for (var i = 0; i < e.clipboardData.files.length; i++) {
      if (e.clipboardData.files.size > 15000) {
        alert(
          "Tamanho de arquivo " +
            e.target.files[i].name +
            " não suportado. Por favor, envie um arquivo menor do que 15mb"
        );
      } else {
        pasteFiles(activeChat, e.clipboardData.files[i]);
      }
    }
  };

  const handleImageChange = (e: any) => {
    if (e.target.files.length !== 0)
      setFileImage(URL.createObjectURL(e.target.files[0]));
  };

  const onaddMessage = (e: any, textMessage: any) => {
    e.preventDefault();
    if (textMessage !== "") {
      settextMessage("");
    }

    if (file.name !== "") {
      setFile({
        name: "",
        size: "",
      });
    }

    if (fileImage !== "") {
      setFileImage("");
    }
  };

  const onData = (recordedBlob: any) => {
    console.log("chunk of real-time data is: ", recordedBlob);
  };

  const onStop = (recordedBlob: any) => {
    if (!deleteAudio) {
      console.log("recordedBlob is: ", recordedBlob);
    } else {
      setDeleteAudio(false);
    }
  };

  return (
    <React.Fragment>
      <div className="p-3 p-lg-4 border-top mb-0">
        <Form onSubmit={(e) => onaddMessage(e, textMessage)}>
          <Row noGutters>
            <Col>
              <div>
                <ReactMic
                  record={record}
                  className={`sound-wave ${record ? "" : "d-none"}`}
                  onStop={onStop}
                  onData={onData}
                  strokeColor="#7a7f9a"
                  backgroundColor="#e6ebf5"
                />
                <Input
                  type="text"
                  value={textMessage}
                  onChange={handleChange}
                  className={`form-control form-control-lg bg-light border-light ${
                    record ? "d-none" : ""
                  }`}
                  placeholder={"Send message..."}
                />
                <div
                  style={{
                    color: "red",
                    fontSize: 12,
                  }}
                >
                  {textMessage.length > 9900
                    ? 10000 - textMessage.length + " caracteres restantes"
                    : null}
                </div>
              </div>
            </Col>
            <Col xs="auto">
              <div className="chat-input-links ml-md-2">
                <ul className="list-inline mb-0">
                  {!record ? (
                    <li className="list-inline-item">
                      <ButtonDropdown
                        className="emoji-dropdown"
                        direction="up"
                        isOpen={isOpen}
                        toggle={toggle}
                      >
                        <DropdownToggle
                          id="emoji"
                          color="link"
                          className="text-decoration-none font-size-16 btn-lg waves-effect"
                        >
                          <i className="ri-emotion-happy-line"></i>
                        </DropdownToggle>
                        <DropdownMenu className="dropdown-menu-lg-right">
                          <Picker set="apple" onSelect={addEmoji} />
                          <Emoji emoji="santa" set="apple" size={16} />
                        </DropdownMenu>
                      </ButtonDropdown>
                      <UncontrolledTooltip target="emoji" placement="top">
                        Emoji
                      </UncontrolledTooltip>
                    </li>
                  ) : (
                    <li className="list-inline-item input-file">
                      <Label
                        id="audio"
                        className={`btn btn-link text-decoration-none font-size-16 btn-lg waves-effect`}
                        onClick={() => {
                          setDeleteAudio(true);
                          setRecord(false);
                        }}
                      >
                        <i className="ri-delete-bin-5-line" />
                      </Label>
                      <UncontrolledTooltip target="audio" placement="top">
                        Áudio
                      </UncontrolledTooltip>
                    </li>
                  )}
                  <li className="list-inline-item input-file">
                    <Label
                      id="audio"
                      className={`btn btn-link text-decoration-none font-size-16 btn-lg waves-effect ${
                        record ? "text-record" : ""
                      }`}
                      onClick={() => setRecord(!record)}
                    >
                      {record ? (
                        <i className="ri-stop-circle-line" />
                      ) : (
                        <i className="ri-mic-line" />
                      )}
                    </Label>
                    <UncontrolledTooltip target="audio" placement="top">
                      Áudio
                    </UncontrolledTooltip>
                  </li>
                  <li className="list-inline-item input-file">
                    <Label
                      id="files"
                      className="btn btn-link text-decoration-none font-size-16 btn-lg waves-effect"
                    >
                      <i className="ri-attachment-line"></i>
                      <Input
                        onChange={(e) => handleFileChange(e)}
                        type="file"
                        name="fileInput"
                      />
                    </Label>
                    <UncontrolledTooltip target="files" placement="top">
                      Attached File
                    </UncontrolledTooltip>
                  </li>
                  <li className="list-inline-item input-file">
                    <Label
                      id="images"
                      className="mr-1 btn btn-link text-decoration-none font-size-16 btn-lg waves-effect"
                    >
                      <i className="ri-image-fill"></i>
                      <Input
                        onChange={(e) => handleImageChange(e)}
                        accept="image/*"
                        type="file"
                        name="fileInput"
                      />
                    </Label>
                    <UncontrolledTooltip target="images" placement="top">
                      Images
                    </UncontrolledTooltip>
                  </li>
                  <li className="list-inline-item">
                    <Button
                      type={textMessage.length < 10000 ? "submit" : "button"}
                      color="primary"
                      className="font-size-16 btn-lg chat-send waves-effect waves-light"
                    >
                      <i className="ri-send-plane-2-fill"></i>
                    </Button>
                  </li>
                </ul>
              </div>
            </Col>
          </Row>
        </Form>
      </div>
    </React.Fragment>
  );
};

export default ChatInput;
