import React, { useState, useEffect, useRef } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
} from "reactstrap";

import SimpleBar from "simplebar-react";
import SelectContact from "./SelectContact";
import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import { useContact } from "../contexts/ContactContext";
import EmailList from "./EmailList";
import IEmailList from "../alias/IEmailList";
import { useConference } from "../contexts/ConferenceContext";
import { getAllTimeDay, today } from '../helpers/DateEasy';
import { time } from "console";

interface IProps {
  show: boolean;
  toggle(): void;
}

export const ModalNewConference: React.FC<IProps> = ({ show, toggle }) => {
  const { t } = useTranslation();
  const { contacts } = useContact();
  const { setCreateConference } = useConference();
  const [isOpenAlert, setIsOpenAlert] = useState(false);
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");
  const [name, setName] = useState<string>("");
  const [description, setDescription] = useState<string>("");
  const [startDate, setStartDate] = useState<string>(today().date);
  const [startTime, setStartTime] = useState<string>(today().time);
  const [duration, setDuration] = useState<string>("01:00");
  const [mdCheck, setMdCheck] = useState<boolean>(false);
  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);
  const [emailList, setEmailList] = useState<IEmailList[]>([]);
  const [parsedDate, setParsedDate] = useState(new Date().toISOString());
  const [flagWrongStartTime, setFlagWrongStartTime] = useState<boolean>(false);
  var DatePicker = require("reactstrap-date-picker");
  const inputName = useRef<HTMLInputElement>(null);

  const refreshTime = () => {
    setTime(today().time);
  }

  const handleCalendarChange = (e: string) => {
    if(e){
      let year = e.split('-')[0];
      let month = e.split('-')[1];
      let day = e.split('-')[2].slice(0,2);
      setParsedDate(year+'-'+month+'-'+day+'T12:00:00.000Z');
      setStartDate(year+'-'+month+'-'+day);
      setStartTime(today().time);
    }
  }
  
  const handleCreateGroup = () => {
    setCreateConference({
      name,
      description,
      startDate,
      startTime,
      duration,
      mdCheck,
      selectedContact,
      emailList,
    });
    
    
    setIsOpenAlert(true);
    setMessage("Criando conferência...");
    
    setTimeout(() => {
      setIsOpenAlert(false);
      setMessage("");
      setSearch("");
      setName("");
      setDescription("");
      setStartDate(today().date);
      setStartTime(today().time);
      setDuration("01:00");
      setMdCheck(false);
      setSelectedContact([]);
      setEmailList([]);
    }, 3000);
    toggle();
  };
  
  const closeAndCleanData = () => {
    setMessage("");
    setSearch("");
    setName("");
    setDescription("");
    setStartDate(today().date);
    setStartTime(today().time);
    setDuration("01:00");
    setMdCheck(false);
    setSelectedContact([]);
    setEmailList([]);
    toggle();
  }
  
  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      setSelectedContact([...selectedContact, contact]);
    } else {
    setSelectedContact(
      selectedContact.filter((sel) => {
        if (sel.id === contact.id) {
          return false;
        }
        return true;
      })
      );
    }
  };
    
  const updatedTime = () => {
    setTime(startTime);
  };
  
  useEffect(updatedTime, [startDate]);
  useEffect(refreshTime, [show]);
    
  const setTime = (value: string) => {
      let time = value.split(":");
      let currentTime = today().time.split(":");
      if (startDate === today().date) {
        if(parseInt(time[0]) < parseInt(currentTime[0])) {
          setFlagWrongStartTime(true);
          return;
        }else{
          setFlagWrongStartTime(false);
        setStartTime(value);
      }

      if(
        parseInt(time[0]) === parseInt(currentTime[0]) &&
        parseInt(time[1]) < parseInt(currentTime[1])
      ){
        setFlagWrongStartTime(true);
        return;
      }else{
        setFlagWrongStartTime(false);
        setStartTime(value);
      }
    }else{
      setFlagWrongStartTime(false);
      setStartTime(value);
    }
  }

  useEffect(() => {
    setTimeout(() => inputName.current?.focus(), 500);
  }, [show]);
  
  return (
    <Modal isOpen={show} size="lg" centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        {t("Create conference")}
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <div className="row">
            <div className="col">
              <FormGroup className="mb-4">
                <Label htmlFor="name-input">{t("Room name")}</Label>
                <input
                  ref={inputName}
                  type="text"
                  className="form-control"
                  id="name-input"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder={t("Room name") + "..."}
                />
                {name === "" &&
                  <div className={"mt-2"} style={{
                  color: "red",
                }}>{t("The field is required")}</div>}
              </FormGroup>
              <FormGroup className="mb-4">
                <Label htmlFor="description-input">
                  {t("Room description")}
                </Label>
                <Input
                  type="text"
                  className="form-control"
                  id="description-input"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  placeholder={t("Room description") + "..."}
                />
              </FormGroup>
              <div className="row">
                <FormGroup className="mb-4 col-6">
                  <Label htmlFor="date-start-input">{t("Starting date")}</Label>
                  <DatePicker
                    id="example-datepicker" 
                    onChange={(e: any) => handleCalendarChange(e)}
                    minDate={new Date().toISOString()}
                    value={parsedDate}
                    showClearButton={false}
                    />
                  </FormGroup>
                <FormGroup className="mb-4 col-6">
                  <Label htmlFor="time-start-input">{t("Starting time")}</Label>
                  <select className="form-control" 
                  value={startTime} onChange={(e) => setTime(e.target.value)}>
                    <option>{today().time}</option>
                    {getAllTimeDay().map((time, i) => {
                      let hiddenOption = true;
                      if(startDate === today().date){
                        let hh = parseInt(time.split(":")[0]);
                        let mm = parseInt(time.split(":")[1]);
                        let hhc = parseInt(today().time.split(":")[0]);
                        let mmc = parseInt(today().time.split(":")[1]);
                        if (hhc < hh) {
                          hiddenOption = false;
                        } else if (hhc === hh) {
                          if (mmc < mm) {
                            hiddenOption = false;
                          }
                        }
                      }else{
                        hiddenOption = false;
                      }
                      if(!hiddenOption){
                        return (
                          <option key={i + Math.random()}>
                            {time}
                          </option>
                        );
                      } 
                      return<></>
                    })}
                  </select>
                  {flagWrongStartTime === true &&
                  <div className={"mt-2"} style={{
                    color: "red",
                  }}>{t("Invalid time")}</div>}
                </FormGroup>
                <div>
                </div>
              </div>
              <div className="row">
                <FormGroup className="mb-4 col-12">
                  <Label htmlFor="duration-input">{t("Duration")}</Label>
                  <Input
                    type="time"
                    className="form-control"
                    id="date-end-input"
                    value={duration}
                    onChange={(e) => setDuration(e.target.value)}
                  />
                </FormGroup>
                <FormGroup className="mb-4 col-12">
                  <div className="custom-control custom-checkbox">
                    <Input
                      type="checkbox"
                      className="custom-control-input"
                      onChange={(e) => {
                        setMdCheck(e.target.checked);
                      }}
                      id={"mdCheck"}
                      value={mdCheck ? 1 : 0}
                    />
                    <Label className="custom-control-label" htmlFor={"mdCheck"}>
                      {t("Wait for call administrator to start")}
                    </Label>
                  </div>
                </FormGroup>
              </div>
            </div>
            <div className="col">
              <FormGroup className="mb-4">
                <Label htmlFor="search-input">{t("Conference Members")}</Label>
                <Input
                  type="text"
                  className="form-control mb-2"
                  id="search-input"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  placeholder={t("Search users..")}
                />
                <div className="border">
                  <SimpleBar style={{ height: "320px" }}>
                    <div id="addContacts">
                      <SelectContact
                        handleCheckContact={handleCheckContact}
                        contacts={contacts}
                        search={search}
                        checkedContacts={selectedContact}
                      />
                    </div>
                  </SimpleBar>
                </div>
              </FormGroup>
            </div>
          </div>
          <hr />
          <h5 className="mb-3">{t("External participants")}</h5>
          <EmailList emailList={emailList} setEmailList={setEmailList} />
        </Form>
        <Alert isOpen={isOpenAlert} color="success">
          {message}
        </Alert>
      </ModalBody>
      <ModalFooter>
        <Button type="button" color="link" onClick={closeAndCleanData}>
          {t("Close")}
        </Button>
        { name !== "" && flagWrongStartTime === false ?
        <Button type="button" color="primary" onClick={handleCreateGroup}>
          {t("Create conference")}
        </Button>
          :
          <Button type="button" color="primary" disabled>
          {t("Create conference")}
        </Button>
      }
      </ModalFooter>
    </Modal>
  );
};

