import React, { CSSProperties, useState } from "react";
import { useCall } from "../contexts/CallContext";
import { tagsRange } from "../services/voice/voicecommunicator";
import { motion } from "framer-motion";
import { height, width } from "../helpers/windowSize";
import { Button } from "reactstrap";

interface DocumentPictureInPicture extends Document {
  pictureInPictureEnabled: boolean;
  pictureInPictureElement: HTMLVideoElementPictureInPicture;
  exitPictureInPicture(): Promise<any>;
}

interface HTMLVideoElementPictureInPicture extends HTMLVideoElement {
  requestPictureInPicture(): Promise<any>;
  disablePictureInPicture: any;
}

const VideoBox: React.FC = () => {
  const toggleOpacity = () => {
    if(flagToTimeOut === true){
      setFlagToOpacity(true);
      setFlagToTimeOut(false);
      setTimeout(() => {
        setFlagToOpacity(false);
        setFlagToTimeOut(true);
      }, 6000);
    }
  }
  const [flagToOpacity, setFlagToOpacity] = useState<boolean>(false);
  const [flagToTimeOut, setFlagToTimeOut] = useState<boolean>(true);
  const [widthBox, setWidthBox] = useState(400);
  const [
    pictureInPictureEnabled,
    setPictureInPictureEnabled,
  ] = useState<boolean>(false);

  const [pictureInPictureTag, setPictureInPictureTag] = useState<
    number | undefined
  >(undefined);

  const { currentCalls } = useCall();

  const togglePictureInPicture = (tagId: number) => {
    const doc = document as DocumentPictureInPicture;
    if (!doc.pictureInPictureElement && doc.pictureInPictureEnabled) {
      const video = document.querySelector(
        `#remote-stream-${tagId}`
      ) as HTMLVideoElementPictureInPicture;
      video?.requestPictureInPicture().catch((error) => {});
    } else {
      if (doc.pictureInPictureEnabled) {
        (document as DocumentPictureInPicture)
          .exitPictureInPicture()
          .catch((error) => {});
      }
    }
  };

  const listenPictureInPictureChanges = () => {
    tagsRange.forEach((tagId) => {
      const videoEl = document.querySelector(`#remote-stream-${tagId}`);
      if (
        videoEl &&
        !(videoEl as HTMLVideoElementPictureInPicture).disablePictureInPicture
      ) {
        videoEl.addEventListener("enterpictureinpicture", () => {
          // Video entered Picture-in-Picture mode.
          setPictureInPictureEnabled(true);
          setPictureInPictureTag(tagId);
        });
        videoEl.addEventListener("leavepictureinpicture", () => {
          // Video left Picture-in-Picture mode.
          setPictureInPictureEnabled(false);
          setPictureInPictureTag(undefined);
        });
      }
    });
  };

  const removeListeners = () => {
    tagsRange.forEach((tagId) => {
      const videoEl = document.querySelector(`#remote-stream-${tagId}`);
      if (videoEl) {
        videoEl.removeEventListener("enterpictureinpicture", () => {});
        videoEl.removeEventListener("leavepictureinpicture", () => {});
      }
    });
  };

  const isVisible = (tagId: number) => {
    const callTag = getCallByTag(tagId);
    return (
      callTag &&
      pictureInPictureTag !== tagId &&
      (callTag.sendingVideo || callTag?.receivingVideo)
    );
  };
  const getCallByTag = (tagId: number) => {
    return Array.from(currentCalls.values()).find(
      (call) => call.tagId === `remote-stream-${tagId}`
    );
  };
  removeListeners();
  listenPictureInPictureChanges();

  const boxVideo: CSSProperties = {
    position: "fixed",
    right: "50px",
    bottom: "50px",
    zIndex: 100,
    maxWidth: `${widthBox}px`,
    borderRadius: "2px",
    backgroundColor: "#000000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  };
  
  const video: CSSProperties = {
    position: "relative",
    width: `${widthBox}px`,
  };
  
  const optionBox: CSSProperties = {
    position: "absolute",
    zIndex: 10,
    top: "2%",
    right: "2%",
    color: "#ffffff",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  const option: CSSProperties = {
    cursor: "pointer",
    marginLeft: "5px",
    fontSize: "18px",
    padding: "5px",
  };
  
  const buttonBox: CSSProperties = {
    position: "absolute",
    zIndex: 10,
    width: "100%",
    bottom: 0,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: 'row',
  };

  const plus: CSSProperties = {
    position: "absolute",
    bottom: "10px",
    right: "10px",
    zIndex: 13,
    color: "black",
  }
  
  const minus: CSSProperties = {
    position: "absolute",
    bottom: "10px",
    right: "30px",
    zIndex: 13,
    color: "white",
  }

  return (
    <div>
      {tagsRange.map((tagId) => (
        <motion.div
          drag
          dragConstraints={{
            top: (height() - 300) * -1,
            bottom: 0,
            left: (width() - 400) * -1,
            right: 0,
          }}
          style={{
            ...boxVideo,
            display: isVisible(tagId) ? boxVideo.display : "none",
          }}
          key={`remote-stream-${tagId}`}
        >
          <div style={optionBox}>
            { flagToOpacity &&
              (document as DocumentPictureInPicture).pictureInPictureEnabled ? (
                <>
                  {!pictureInPictureEnabled && (
                    <div
                      style={option}
                      onClick={() => togglePictureInPicture(tagId)}
                    >
                      <i className="ri-picture-in-picture-2-line"></i>
                    </div>
                  )}
                  {pictureInPictureTag === tagId && (
                    <div
                      style={option}
                      onClick={() => togglePictureInPicture(tagId)}
                    >
                      <i className="ri-picture-in-picture-exit-line"></i>
                    </div>
                  )}
                </>
              ) : null
            }

            {flagToOpacity && <div
              style={option}
              onClick={() => {
                const call = getCallByTag(tagId);
                call?.endCall && call.endCall();
              }}
            >
              <i className="ri-close-line"></i>
            </div>}
          </div>


          
          {
            flagToOpacity && 
            <>
              <div style={buttonBox}>
              <Button
                type="button"
                color="none"
                onClick={() => {
                  const call = getCallByTag(tagId);
                  call?.endCall && call.endCall();
                }}
                className="nav-btn user-profile-show btn-head-conference red"
              >
                <i className="ri-phone-line"></i>
              </Button>

              <Button
                type="button"
                color="none"
                onClick={() => {
                  if (getCallByTag(tagId)?.micMuted)
                    getCallByTag(tagId)?.unMuteMic();
                  else getCallByTag(tagId)?.muteMic();
                }}
                className={`nav-btn user-profile-show btn-head-conference ${
                  getCallByTag(tagId)?.micMuted ? "red" : "white"
                }`}
              >
                {!getCallByTag(tagId)?.micMuted ? (
                  <i className="ri-mic-line"></i>
                ) : (
                  <i className="ri-mic-off-line"></i>
                )}
              </Button>

              <Button
                type="button"
                color="none"
                onClick={() => {
                  if (getCallByTag(tagId)?.hold)
                    getCallByTag(tagId)?.unHoldCall();
                  else getCallByTag(tagId)?.holdCall();
                }}
                className={`nav-btn user-profile-show btn-head-conference ${
                  getCallByTag(tagId)?.hold ? "red" : "white"
                }`}
              >
                {getCallByTag(tagId)?.hold ? (
                  <i className="ri-play-line"></i>
                ) : (
                  <i className="ri-pause-line"></i>
                )}
              </Button>

              <Button
                type="button"
                color="none"
                onClick={() => {
                  if (getCallByTag(tagId)?.sendingVideo) {
                    if (getCallByTag(tagId)?.videoMuted)
                      getCallByTag(tagId)?.enableCam();
                    else getCallByTag(tagId)?.disableCam();
                  } else {
                    alert(
                      "Mudança de tipo de chamada ainda não está disponivel :("
                    );
                  }
                }}
                className={`nav-btn user-profile-show btn-head-conference white ${
                  getCallByTag(tagId)?.videoMuted ? "red" : "white"
                }`}
              >
                {getCallByTag(tagId)?.videoMuted ? (
                  <i className="ri-camera-off-line"></i>
                ) : (
                  <i className="ri-camera-line"></i>
                )}
              </Button>
            </div>

            <div style={plus}>
            <span
                style={{ ...option, fontSize: "14px" }}
                onClick={() => setWidthBox(widthBox + 100)}
              
              >
                <i style={{backgroundColor: "white"}} className="ri-add-line black"></i>
            </span>
            </div>

            <div style={minus}> 
            <span
                style={{ ...option, fontSize: "14px" }}
                onClick={() => setWidthBox(widthBox - 100)}
            >
              <i style={{backgroundColor: "white"}} className="ri-subtract-line black"></i>
            </span>
            </div>
          </>
          }
          

         

          <video
            style={video}
            onMouseOver={toggleOpacity}
            id={`remote-stream-${tagId}`}
            hidden={!isVisible(tagId)}
          />
        </motion.div>
      ))}
    </div>
  );
};

export default VideoBox;
