import React, { createContext, useContext, useState } from "react";
import { activeChat as activeChatData } from "./../data/chat";

//alias
import IChat from "../alias/IChat";
import IContact from "../alias/IContact";
import IGroup from "../alias/IGroup";
import IMessage from "../alias/IMessage";

interface IChatContextData {
  showChat: boolean;
  setShowChat(value: boolean): void;
  activeChat: IChat;
  setActiveChat(value: IChat): void;
  openChat(obj: IChat): void;
  chats: IChat[];
  setChats(value: IChat[]): void;
  forwardMessageList: (IContact | IGroup)[] | undefined;
  setForwardMessageList(value: (IContact | IGroup)[] | undefined): void;
  replyMsg: IMessage | undefined;
  setReplyMsg(msg: IMessage | undefined): void;
}

export const ChatContext = createContext<IChatContextData>(
  {} as IChatContextData
);

export const ChatProvider: React.FC = ({ children }) => {
  const [showChat, setShowChat] = useState(false);
  const [activeChat, setActiveChat] = useState(activeChatData);
  const [chats, setChats] = useState<IChat[]>([]);
  const [forwardMessageList, setForwardMessageList] = useState<
    (IContact | IGroup)[] | undefined
  >();
  const [replyMsg, setReplyMsg] = useState<IMessage | undefined>();

  const openChat = (obj: IChat) => {
    if (activeChat?.jid === obj.jid) return;
    setShowChat(true);
    setChats(
      chats.map((item) => {
        if (item.jid === obj.jid) {
          item.unRead = 0;
          item.messageLoaded = item.messages.length >= 20 ? false : true;
          setActiveChat(item);
        }
        return item;
      })
    );
  };

  return (
    <ChatContext.Provider
      value={{
        showChat,
        setShowChat,
        activeChat,
        setActiveChat,
        openChat,
        chats,
        setChats,
        forwardMessageList,
        setForwardMessageList,
        replyMsg,
        setReplyMsg,
      }}
    >
      {children}
    </ChatContext.Provider>
  );
};

export function useChat() {
  return useContext(ChatContext);
}
