import React, { useState, useEffect } from "react";
import IMessage from "../alias/IMessage";

import VideoMessage from "./VideoMessage";
import ImageMessage from "./ImageMessage";
import AudioMessage from "./AudioMessage";
import FileMessage from "./FileMessage";
import { useTranslation } from "react-i18next";
import GroupNotification from './GroupNotification';
import "./../assets/css/link-message-text.css";

import Linkify from "react-linkify";
import { parseEmojis } from "../helpers/MessageUtils";
interface IProps {
  message: IMessage;
}

const ChatMessageText: React.FC<IProps> = ({ message }) => {
  const [show, setShow] = useState(false);
  const { t } = useTranslation();
  const toggle = () => setShow(!show);
  const componentDecorator = (href: string, text: string, key: any) => (
    <a href={href} key={key} rel="noopener noreferrer" target="_blank">
      {text}
    </a>
  );
  useEffect(() => {
    setShow(false);
  }, [message]);

  var smallerMessage = "";
  var biggerMessage = "";
  const MAXNUMBEROFCHARACTERSONAMESSAGE = 10000;
  const MAXNUMBERTODISPLAYMESSAGE = 165;
  if(!message.eventId){
    if (message.message) {
      if (
        message.message.length > MAXNUMBERTODISPLAYMESSAGE &&
        !message.isFileMessage &&
        !message.isImageMessage &&
        !message.isAudioMessage &&
        !message.isVideoMessage
      ) {
        return (
          <div
            id="receivedmessage"
            className="mb-0"
            style={{ wordBreak: "break-word", textAlign: "left" }}
          >
            <Linkify componentDecorator={componentDecorator}>
              {!show
                ? (smallerMessage = message.message.slice(
                    0,
                    MAXNUMBERTODISPLAYMESSAGE
                  )) && smallerMessage
                : null}
              {show
                ? (biggerMessage = message.message.slice(
                    MAXNUMBERTODISPLAYMESSAGE,
                    MAXNUMBEROFCHARACTERSONAMESSAGE
                  )) &&
                  message.message.slice(0, MAXNUMBERTODISPLAYMESSAGE) +
                    biggerMessage
                : null}

              <button
                className="hover"
                id="more-msg"
                style={{
                  border: 0,
                  background: 0,
                }}
                onClick={toggle}
              >
                {!show ? ".. " + t("Read more") : null}
              </button>
            </Linkify>
          </div>
        );
      }

      if (
        message.message.length <= 164 ||
        message.isFileMessage ||
        message.isImageMessage ||
        message.isAudioMessage ||
        message.isVideoMessage
      ) {
        return (
          <div
            className="mb-0"
            id="receivedmessage"
            style={{ wordBreak: "break-word", textAlign: "left" }}
          >
            {message.isAudioMessage && <AudioMessage url={message.fileUrl} />}
            {message.isFileMessage && <FileMessage url={message.fileUrl} />}
            {message.isVideoMessage && <VideoMessage url={message.fileUrl} />}
            {message.isImageMessage && <ImageMessage url={message.fileUrl} />}
            {!message.isAudioMessage &&
              !message.isFileMessage &&
              !message.isVideoMessage &&
              !message.isImageMessage && (
                <Linkify componentDecorator={componentDecorator}>
                  {message.message
                    ? message.message
                        .toString()
                        .split("\n")
                        .map((msg, i) => (
                          <div key={msg + i}>{parseEmojis(msg)}</div>
                        ))
                    : null}
                </Linkify>
              )}
          </div>
        );
      }
    }
  }else{
    if(message.eventId){
        return <GroupNotification msg={message}/>
      }
    }
  return <></>;
};

export default ChatMessageText;
