import React from "react";

//Import Components
import { useCall } from "../contexts/CallContext";
import { useMainBox } from "../contexts/MainBoxContext";
import CallStatus from "../enuns/CallStatus";
import ScreenType from "../enuns/ScreenType";
import CallManager from "./CallManager";
import ChatBox from "./ChatBox";
import ConferenceBox from "./ConferenceBox";
import ParticipantsDrawer from "./ParticipantsDrawer";
import VideoBox from "./VideoBox";
import Welcome from "./Welcome";

const MainBox = () => {
  const { currentCalls } = useCall();
  const { screen } = useMainBox();

  function showScreen() {
    switch (screen) {
      case ScreenType.WELCOME:
        return <Welcome />;
      case ScreenType.CHAT:
        return <ChatBox />;
      case ScreenType.CALL_MANAGER:
        return <CallManager />;
      case ScreenType.CONFERENCE_BOX:
        return <ConferenceBox />;
    }
  }

  return (
    <React.Fragment>
      {showScreen()}
      <VideoBox />
      <ParticipantsDrawer />
    </React.Fragment>
  );
};

export default MainBox;
