import React from "react";
import { Media, Button, Row, Col } from "reactstrap";
import { Link } from "react-router-dom";
import { useChat } from "../contexts/ChatContext";
import { useTranslation } from "react-i18next";
import ReactImageFallback from "react-image-fallback";
import defaultAvatar from "../assets/images/default.png";
import { onImgError } from "../helpers/ImageUtils";
import { useGroup } from "../contexts/GroupContext";
import { useContact } from "../contexts/ContactContext";
import { zeroLeft } from "../helpers/zeroLeft";
import { useCall } from "../contexts/CallContext";
import CallStatus from "../enuns/CallStatus";

interface IProps {
  chatProfileSidebarOpen: boolean;
  setChatProfileSidebarOpen(value: boolean): void;
}

const ChatHeadConference: React.FC<IProps> = ({
  chatProfileSidebarOpen,
  setChatProfileSidebarOpen,
}) => {
  const { chats, activeChat, setChats, setShowChat } = useChat();
  const { groups, setGroups } = useGroup();
  const { contacts, setContacts } = useContact();
  const { currentCalls } = useCall();

  const ChatProfileSidebar = () => {
    setChatProfileSidebarOpen(!chatProfileSidebarOpen);
  };

  function closeUserChat() {
    setShowChat(false);
  }

  function segundosParaHora(sec: number) {
    var horas = Math.floor(sec / (60 * 60));

    var resto = sec % (60 * 60);
    var minutos = Math.floor(sec / 60);

    resto %= 60;
    var segundos = Math.ceil(resto);

    return `${zeroLeft(horas, 2)}:${zeroLeft(minutos, 2)}:${zeroLeft(
      segundos,
      2
    )}`;
  }

  const { t } = useTranslation();
  const currentCall = Array.from(currentCalls.values()).find(
    (call) => call.callerObject?.jid === activeChat.jid
  );
  return (
    <React.Fragment>
      <div
        className="p-3 border-bottom"
        style={{ backgroundColor: "#e6ebf5", height: "89px" }}
      >
        <Row className="align-items-center">
          <Col sm={4} xs={8}>
            <Media className="align-items-center">
              <div className="d-block d-lg-none mr-2">
                <Link
                  to="#"
                  onClick={() => closeUserChat()}
                  className="user-chat-remove text-muted font-size-16 p-2"
                >
                  <i className="ri-arrow-left-s-line"></i>
                </Link>
              </div>
              {activeChat.profilePicture ? (
                <div className="mr-3">
                  <ReactImageFallback
                    src={activeChat.profilePicture}
                    onError={() => {
                      if (activeChat.jid.includes("@conference."))
                        onImgError(
                          chats,
                          groups,
                          contacts,
                          setChats,
                          setGroups,
                          setContacts,
                          activeChat
                        );
                    }}
                    style={{ maxHeight: "40px", maxWidth: "40px" }}
                    fallbackImage={defaultAvatar}
                    className="rounded-circle avatar-lg img-thumbnail"
                    alt="chatvia"
                  />
                </div>
              ) : (
                <div className="chat-user-img align-self-center mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {activeChat.name.trim().charAt(0)}
                    </span>
                  </div>
                </div>
              )}

              <Media body className="overflow-hidden">
                <h5 className="font-size-16 mb-0 text-truncate">
                  <Link
                    to="#"
                    onClick={() => ChatProfileSidebar()}
                    className="text-reset user-profile-show"
                  >
                    <div className="p-1">
                      <div>{activeChat.name}</div>
                      {currentCall?.isGroup && (
                        <div className="text-muted h7 mt-1">
                          Kaline, Vinicius, Lucas, Romero, Daniel e 2+
                        </div>
                      )}
                    </div>
                  </Link>
                </h5>
              </Media>
            </Media>
          </Col>
          <Col sm={8} xs={4}>
            <ul className="list-inline user-chat-nav text-right mb-0">
              <li className="list-inline-item d-none d-lg-inline-block">
                <div className="d-flex justify-content-center align-items-center">
                  {currentCall?.callStatus === CallStatus.CONNECTING && (
                    <span className="h4 mr-3">{t("Connecting...")}</span>
                  )}
                  {currentCall?.callStatus === CallStatus.CALLING && (
                    <span className="h4 mr-3">{t("Calling...")}</span>
                  )}
                  {currentCall?.callStatus === CallStatus.ON_GOING && (
                    <>
                      <span className="h4 mr-3">{t("Call In Progress")}</span>
                      <span
                        className="h4 d-flex justify-content-center align-items-center"
                        style={{ width: "100px" }}
                      >
                        {segundosParaHora(
                          currentCall?.seconds ? currentCall?.seconds : 0
                        )}
                      </span>
                    </>
                  )}
                </div>
              </li>
              <li className="list-inline-item d-none d-lg-inline-block ml-3">
                <Button
                  type="button"
                  color="none"
                  onClick={() => currentCall?.endCall && currentCall.endCall()}
                  className="nav-btn user-profile-show btn-head-conference red"
                >
                  <i className="ri-phone-line"></i>
                </Button>
              </li>

              <li className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    if (currentCall?.micMuted) currentCall?.unMuteMic();
                    else currentCall?.muteMic();
                  }}
                  className={`nav-btn user-profile-show btn-head-conference ${
                    currentCall?.micMuted ? "red" : "white"
                  }`}
                >
                  {!currentCall?.micMuted ? (
                    <i className="ri-mic-line"></i>
                  ) : (
                    <i className="ri-mic-off-line"></i>
                  )}
                </Button>
              </li>

              <li className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    if (currentCall?.hold) currentCall?.unHoldCall();
                    else currentCall?.holdCall();
                  }}
                  className={`nav-btn user-profile-show btn-head-conference ${
                    currentCall?.hold ? "red" : "white"
                  }`}
                >
                  {currentCall?.hold ? (
                    <i className="ri-play-line"></i>
                  ) : (
                    <i className="ri-pause-line"></i>
                  )}
                </Button>
              </li>

              <li className="list-inline-item d-none d-lg-inline-block mr-3">
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    if (currentCall?.sendingVideo) {
                      if (currentCall?.videoMuted) currentCall?.enableCam();
                      else currentCall?.disableCam();
                    } else {
                      alert(
                        "Mudança de tipo de chamada ainda não está disponivel :("
                      );
                    }
                  }}
                  className={`nav-btn user-profile-show btn-head-conference white ${
                    currentCall?.videoMuted ? "red" : "white"
                  }`}
                >
                  {currentCall?.videoMuted ? (
                    <i className="ri-camera-off-line"></i>
                  ) : (
                    <i className="ri-camera-line"></i>
                  )}
                </Button>
              </li>

              <li className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => ChatProfileSidebar()}
                  className="nav-btn user-profile-show d-flex justify-content-center align-items-center h-100"
                >
                  <i className="ri-user-2-line"></i>
                </Button>
              </li>
            </ul>
          </Col>
        </Row>
      </div>
    </React.Fragment>
  );
};

export default ChatHeadConference;
