import React, { createContext, useContext, useState } from "react";
import IMessage from "../alias/IMessage";

interface IModalContext {
  showModalForwardMessageTo: IMessage | undefined;
  setShowModalForwardMessageTo(value: IMessage | undefined): void;
  showModalEditInfo: boolean;
  setShowModalEditInfo(value:boolean): void;
  showModalChangePassword: boolean;
  setShowModalChangePassword(value: boolean): void;
}

export const ModalContext = createContext<IModalContext>({} as IModalContext);

export const ModalProvider: React.FC = ({ children }) => {
  const [showModalForwardMessageTo, setShowModalForwardMessageTo] = useState<IMessage | undefined>();

  const [showModalEditInfo, setShowModalEditInfo] = useState(false);

  const [showModalChangePassword, setShowModalChangePassword] = useState(false);
  return (
    <ModalContext.Provider
      value={{ showModalForwardMessageTo, 
        setShowModalForwardMessageTo, 
        showModalEditInfo, 
        setShowModalEditInfo, 
        showModalChangePassword,
        setShowModalChangePassword, }}
    >
      {children}
    </ModalContext.Provider>
  );
};

export function useModal() {
  return useContext(ModalContext);
}
