import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
} from "reactstrap";

import { useTranslation } from "react-i18next";
import { useModal } from "./../contexts/ModalContext";

export const ModalChangePassword: React.FC = () => {
  const { t } = useTranslation();

  const { showModalChangePassword, setShowModalChangePassword } = useModal();
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");

  const toggle = () => {
    setShowModalChangePassword(!showModalChangePassword);
  };

  return (
    <Modal isOpen={showModalChangePassword} centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        Alterar Senha
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <FormGroup className="mb-4">
            <Label htmlFor="current-input">Senha atual</Label>
            <Input
              type="text"
              className="form-control"
              id="current-input"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              placeholder="Senha atual"
            />
          </FormGroup>
          <FormGroup className="mb-4">
            <Label htmlFor="new-input">Nova senha</Label>
            <Input
              type="text"
              className="form-control"
              id="new-input"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Nova senha"
            />
          </FormGroup>
          <FormGroup className="mb-4">
            <Label htmlFor="confirm-input">Confirmar nova senha</Label>
            <Input
              type="text"
              className="form-control"
              id="confirm-input"
              value={confirmNewPassword}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
              placeholder="Confirmar nova senha"
            />
          </FormGroup>
        </Form>
      </ModalBody>
      <ModalFooter>
        <Button
          type="button"
          color="link"
          onClick={() => setShowModalChangePassword(false)}
        >
          {t("Close")}
        </Button>
        <Button type="button" color="primary" onClick={() => {}}>
          Alterar Senha
        </Button>
      </ModalFooter>
    </Modal>
  );
};
