import React, { useState } from "react";
import {
  Dropdown,
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  Card,
  Button,
  InputGroup, 
  InputGroupAddon, 
  Input,
  Tooltip
} from "reactstrap";

import SimpleBar from "simplebar-react";

//Import components
import CustomCollapse from "./CustomCollapse";

//Import Images
import avatar1 from "./../assets/images/users/avatar-1.jpg";

//i18n
import { useTranslation } from "react-i18next";
import { useAuth } from "../contexts/AuthContext";
import { ModalChangePassword } from "./ModalChangePassword";

import { useModal } from "./../contexts/ModalContext";
import ModalEditInfo from "./ModalEditInfo";
import { getUserStatusName } from "../helpers/StatusUtils";
import ModalConfigDevices from "./ModalConfigDevices";
import Swal from "sweetalert2";

function SettingTab() {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [isOpen1, setIsOpen1] = useState(true);
  const [isOpen2, setIsOpen2] = useState(false);
  const [isOpen3, setIsOpen3] = useState(false);

  const [busy, setBusy] = useState("");
  const [noAnswer, setNoAnswer] = useState("");
  const [notRegistered, setNotRegistered] = useState("");
  const [followMeAlways, setFollowMeAlways] = useState("");

  const [ lockedBusyInput, setLockedBusy ] = useState(true);
  const [ lockedNoAnswerInput, setLockedNoAnswer ] = useState(true);
  const [ lockedNotRegisteredInput, setLockedNotRegistered ] = useState(true);
  const [ lockedFollowMeAlwaysInput, setLockedFollowMeAlways ] = useState(true);

  const { user, setUser } = useAuth();
  const { t } = useTranslation();
  const {
    setShowModalEditInfo,
    setShowModalChangePassword,
    setShowModalDevicesConfig,
  } = useModal();

  const circleColor = () => {
    if (user.status === "online") return "text-success";
    else if (user.status === "away") return "text-warning";
    else return "text-danger";
  };

  const toggleCollapse1 = () => {
    setIsOpen1(true);
    setIsOpen2(false);
    setIsOpen3(false);
  };
  const toggleCollapse2 = () => {
    setIsOpen1(false);
    setIsOpen2(true);
    setIsOpen3(false);
  };
  const toggleCollapse3 = () => {
    setIsOpen1(false);
    setIsOpen2(false);
    setIsOpen3(true);
  };

  const toggle = () => setDropdownOpen(!dropdownOpen);

  const setUserStatus = (status: string) => {
    setUser({ ...user, status });
  };

  const [tooltipOpen1, setTooltipOpen1] = useState(false);
  const [tooltipOpen2, setTooltipOpen2] = useState(false);
  const [tooltipOpen3, setTooltipOpen3] = useState(false);
  const [tooltipOpen4, setTooltipOpen4] = useState(false);

  const toggleTooltip1 = () => setTooltipOpen1(!tooltipOpen1);
  const toggleTooltip2 = () => setTooltipOpen2(!tooltipOpen2);
  const toggleTooltip3 = () => setTooltipOpen3(!tooltipOpen3);
  const toggleTooltip4 = () => setTooltipOpen4(!tooltipOpen4);

  /* const isThisStringNotEmpty = (value: string) => {
    if(value !== ""){
      return true;
    }else{
      return false
    }
  } */

  const isEveryDataLocked = () => {
    if(
      lockedBusyInput === true &&
      lockedNoAnswerInput === true &&
      lockedNotRegisteredInput === true &&
      lockedFollowMeAlwaysInput === true
    ){
      return true;
    }else{
      return false;
    }
  }
  
  const isEveryDataEmpty = () => {
    if(
      busy === "" &&
      noAnswer === "" &&
      notRegistered === "" &&
      followMeAlways === ""
    ){
      return true
    }else{
      return false;
    }
  }

  return (
    <React.Fragment>
      <ModalChangePassword />
      <div>
        <div className="px-4 pt-4">
          <h4 className="mb-0">{t("Settings")}</h4>
        </div>

        <div className="text-center border-bottom p-4">
          <div className="mb-4 profile-user">
            <img
              src={user.profilePicture || avatar1}
              className="rounded-circle avatar-lg img-thumbnail"
              alt="chatvia"
            />
            <Button
              type="button"
              color="light"
              className="avatar-xs p-0 rounded-circle profile-photo-edit"
            >
              <i className="ri-pencil-fill"></i>
            </Button>
          </div>
          <h5 className="font-size-16 mb-1 text-truncate">{user.name}</h5>
          <Dropdown
            isOpen={dropdownOpen}
            toggle={toggle}
            className="d-inline-block mb-1"
          >
            <DropdownToggle tag="a" className="text-muted pb-1 d-block">
              <i
                className={`ri-record-circle-fill font-size-10 ${circleColor()} mr-1 d-inline-block`}
              ></i>{" "}
              {t(getUserStatusName(user.status))}{" "}
              <i className="mdi mdi-chevron-down"></i>
            </DropdownToggle>

            <DropdownMenu>
              <DropdownItem
                onClick={() => {
                  setUserStatus("online");
                }}
              >
                {t("Online")}
              </DropdownItem>
              <DropdownItem
                onClick={() => {
                  setUserStatus("away");
                }}
              >
                {t("Away")}
              </DropdownItem>
              <DropdownItem
                onClick={() => {
                  setUserStatus("dnd");
                }}
              >
                {t("Do not Disturb")}
              </DropdownItem>
            </DropdownMenu>
          </Dropdown>
        </div>
        {/* End profile user */}

        {/* Start User profile description */}
        <SimpleBar
          style={{ maxHeight: "100%" }}
          className="p-4 user-profile-desc"
        >
          <div id="profile-setting-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              <CustomCollapse
                title="Personal Info"
                isOpen={isOpen1}
                iconClass=""
                toggleCollapse={toggleCollapse1}
              >
                <div className="float-right">
                  <Button
                    onClick={() => setShowModalEditInfo(true)}
                    color="light"
                    size="sm"
                    type="button"
                  >
                    <i className="ri-edit-fill mr-1 align-middle"></i>{" "}
                    {t("Edit")}
                  </Button>
                </div>
                <div>
                  <p className="text-muted mb-1">{t("Name")}</p>
                  <h5 className="font-size-14">{user.name}</h5>
                </div>
                <div className="float-right">
                  <Button
                    onClick={() => setShowModalChangePassword(true)}
                    color="light"
                    size="sm"
                    type="button"
                  >
                    <i className="ri-edit-fill mr-1 align-middle"></i>{" "}
                    {t("Edit security")}
                  </Button>
                </div>
                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Email")}</p>
                  <h5 className="font-size-14">{user.email}</h5>
                </div>

                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Number")}</p>
                  <h5 className="font-size-14">{user.number}</h5>
                </div>
                {/* 
                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Time")}</p>
                  <h5 className="font-size-14">{t("11:40 AM")}</h5>
                </div>

                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Location")}</p>
                  <h5 className="font-size-14 mb-0">{t("California, USA")}</h5>
                </div> */}
              </CustomCollapse>
            </Card>
          </div>
          <div id="profile-setting-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              <CustomCollapse
                title={t("Devices Settings")}
                isOpen={isOpen2}
                iconClass=""
                toggleCollapse={toggleCollapse2}
              >
                <Button
                  color="light"
                  size="sm"
                  type="button"
                  block
                  onClick={() => {
                    setShowModalDevicesConfig(true);
                  }}
                >
                  <i className="ri-edit-fill mr-1 align-middle"></i>{" "}
                  {t("Change devices settings")}
                </Button>
              </CustomCollapse>
            </Card>
          </div>
          <div id="profile-setting-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              <CustomCollapse
                title={t("Telephone Extension Line Settings")}
                isOpen={isOpen3}
                iconClass=""
                toggleCollapse={toggleCollapse3}
              >    

                <div className="mb-2 text-muted">Follow me: </div>

                <div>
                  <div className={"mb-1 mt-3"} style={{fontWeight: "bold", display: "flex", flexDirection: "row", alignItems: "center"}}>
                      Busy: 
                    <div id="TooltipExample1" className={"ml-1 mb-1"}>
                      <img alt={"?"} src={require('./../assets/images/doubt.png')}/>
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                    {lockedBusyInput === false ? 
                      <Button style={{backgroundColor: "#43a552", border: "none"}} onClick={() =>{
                        setLockedBusy(!lockedBusyInput)
                        setBusy("");
                        }
                      }>
                      <div style={{fontWeight: "bold", color: "white"}} className="ri-check-fill"></div>
                      </Button>
                        :
                      <Button style={{backgroundColor: "#fb9f2b", border: "none"}} onClick={() => setLockedBusy(!lockedBusyInput)}>
                        <div style={{fontWeight: "bold", color: "black"}} className="ri-close-fill"></div>
                      </Button> 
                    }
                  </InputGroupAddon>
                  {
                    lockedBusyInput ?
                    <Input placeholder={t("Destination")} disabled />
                      :
                    <Input placeholder={t("Destination")} onChange={(e) => setBusy(e.target.value)} />
                  }
                </InputGroup>

                <div>
                  <div className={"mb-1 mt-3"} style={{fontWeight: "bold", display: "flex", flexDirection: "row", alignItems: "center"}}>
                      No answer: 
                    <div id="TooltipExample2" className={"ml-1 mb-1"}>
                      <img alt={"?"} src={require('./../assets/images/doubt.png')}/>
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                    {lockedNoAnswerInput === false  ?
                    <Button style={{backgroundColor: "#43a552", border: "none"}} onClick={() => setLockedNoAnswer(!lockedNoAnswerInput)}>
                      <div style={{fontWeight: "bold", color: "white"}} className="ri-check-fill"></div>
                    </Button> 
                      :
                    <Button style={{backgroundColor: "#fb9f2b", border: "none"}} onClick={() => setLockedNoAnswer(!lockedNoAnswerInput)}>
                      <div style={{fontWeight: "bold", color: "black"}} className="ri-close-fill"></div>
                    </Button>
                    }
                  </InputGroupAddon>
                    {
                      lockedNoAnswerInput ?
                      <Input placeholder={t("Destination")} disabled />
                        :
                      <Input placeholder={t("Destination")} onChange={(e) => setNoAnswer(e.target.value)} />
                    }
                </InputGroup>

                <div>
                  <div className={"mb-1 mt-3"} style={{fontWeight: "bold", display: "flex", flexDirection: "row", alignItems: "center"}}>
                      Not registered extension line: 
                    <div id="TooltipExample3" className={"ml-1 mb-1"}>
                      <img alt={"?"} src={require('./../assets/images/doubt.png')}/>
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                    {lockedNotRegisteredInput === false  ?
                    <Button style={{backgroundColor: "#43a552", border: "none"}} onClick={() => setLockedNotRegistered(!lockedNotRegisteredInput)}>
                      <div style={{fontWeight: "bold", color: "white"}} className="ri-check-fill"></div>
                    </Button>
                      :
                    <Button style={{backgroundColor: "#fb9f2b", border: "none"}} onClick={() => setLockedNotRegistered(!lockedNotRegisteredInput)}>
                      <div style={{fontWeight: "bold", color: "black"}} className="ri-close-fill"></div>
                    </Button>
                    }
                  </InputGroupAddon>
                  {
                    lockedNotRegisteredInput ? 
                    <Input placeholder={t("Destination")} disabled/>
                      :
                    <Input placeholder={t("Destination")} onChange={(e) => setNotRegistered(e.target.value)}/>
                  }
                </InputGroup>

                <div>
                  <div className={"mb-1 mt-3"} style={{fontWeight: "bold", display: "flex", flexDirection: "row", alignItems: "center"}}>
                      Always: 
                    <div id="TooltipExample4" className={"ml-1 mb-1"}>
                      <img alt={"?"} src={require('./../assets/images/doubt.png')}/>
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                  {lockedFollowMeAlwaysInput === false  ?
                  <Button style={{backgroundColor: "#43a552", border: "none"}} onClick={() => setLockedFollowMeAlways(!lockedFollowMeAlwaysInput)}>
                    <div style={{fontWeight: "bold", color: "white"}} className="ri-check-fill"></div>
                  </Button>
                    :
                  <Button style={{backgroundColor: "#fb9f2b", border: "none"}} onClick={() => setLockedFollowMeAlways(!lockedFollowMeAlwaysInput)}>
                    <div style={{fontWeight: "bold", color: "black"}} className="ri-close-fill"></div>
                  </Button>
                  } 
                </InputGroupAddon>
                    { 
                      lockedFollowMeAlwaysInput ? 
                      <Input placeholder={t("Destination")} disabled />
                        :
                      <Input placeholder={t("Destination")} onChange={(e) => setFollowMeAlways(e.target.value)} />
                    }
                </InputGroup>

                <Tooltip placement="right" isOpen={tooltipOpen1} target="TooltipExample1" toggle={toggleTooltip1}>
                  Quando o ramal estiver ocupado a chamada será encaminhada para o ramal adicionado abaixo
                </Tooltip>

                <Tooltip placement="right" isOpen={tooltipOpen2} target="TooltipExample2" toggle={toggleTooltip2}>
                  Quando o ramal não atender a chamada será encaminhada para o ramal adicionado abaixo
                </Tooltip>

                <Tooltip placement="right" isOpen={tooltipOpen3} target="TooltipExample3" toggle={toggleTooltip3}>
                  Quando o ramal não estiver registrado no PABX
                </Tooltip>

                <Tooltip placement="right" isOpen={tooltipOpen4} target="TooltipExample4" toggle={toggleTooltip4}>
                  Siga-me sempre
                </Tooltip>

                {isEveryDataEmpty() || isEveryDataLocked() ? 
                  <Button
                  color="light"
                  size="sm"
                  type="button"
                  block
                  disabled
                  className={"mt-4"}
                >
                  {t("Save")}
                </Button> 
                  :
                  <Button
                    color="light"
                    size="sm"
                    type="button"
                    block
                    onClick={() => {
                      Swal.fire({
                        title: 'Agora, consulta-se a API para pegar o status atual de configuração!',
                        //Romero, para facilitar: os states utilizados foram: {busy, noAnswer, notRegistered, followMeAlways}
                        showClass: {
                          popup: 'animate__animated animate__fadeInDown'
                        },
                        hideClass: {
                          popup: 'animate__animated animate__fadeOutUp'
                        }
                      });
                    }}
                    className={"mt-4"}
                  >
                    {t("Save")}
                  </Button>
                  }
              </CustomCollapse>
            </Card>
          </div>
        </SimpleBar>
      </div>
      <ModalEditInfo />
      <ModalConfigDevices />
    </React.Fragment>
  );
}

export default SettingTab;
