import React, { useState } from "react";
import { Row, Col } from "reactstrap";

const ChatInputAnswer = () => {
  const [show, setShow] = useState(false);

  if (!show) return <></>;

  return (
    <div className="p-2 border-top mb-0">
      <Row noGutters>
        <Col sm={10} className="offset-1">
          <div className={`bg-light pt-1 pb-1 pr-3 pl-3 border-radius`}>
            <div>
              <strong>Lucas Carvalho</strong>
            </div>
            <div>
              <small className="text-muted">
                Lorem Ipsum is simply dummy text of the printing and typesetting
                industry. Lorem Ipsum has been the industry's standard dummy
                text ever since the 1500s, when an unknown printer took a galley
                of type and scrambled it to make a type specimen book....
              </small>
            </div>
          </div>
        </Col>
        <Col
          sm={1}
          className="d-flex justify-content-center align-items-center"
        >
          <div className="btn-close-answer" onClick={() => setShow(false)}>
            <i className="ri-close-line"></i>
          </div>
        </Col>
      </Row>
    </div>
  );
};

export default ChatInputAnswer;
