import IContact from "../alias/IContact";
import IGroup from "../alias/IGroup";

export const SearchContactAndGroup = (
  contacts: (IContact | IGroup)[],
  search: string
) => {
  if(
    search.includes('@') || 
    search.includes('(') || 
    search.includes(')') || 
    search.includes('[') || 
    search.includes(']') ||
    search.includes('+') || 
    search.includes('-') || 
    search.includes('/') || 
    search.includes('?') || 
    search.includes('*') ||
    search.includes(';') ||
    search.includes(':') ||
    search.includes(',') ||
    search.includes('.') ||
    search.includes('|') ||
    search.includes('=') ||
    search.includes('!') ||
    search.includes('#') ||
    search.includes('$') ||
    search.includes('%') ||
    search.includes('¨') ||
    search.includes('&') ||
    search.includes('_') ||
    search.includes('{') ||
    search.includes('}') ||
    search.includes('º') ||
    search.includes('ª')
  ){
    return [];
  }else{
    return contacts.filter((item) => {
      return item.name.toUpperCase().match(search.toUpperCase());
    });
  }
};
